import { NextApiResponse } from 'next';
import prisma from '@/lib/prisma';
import { withAuth, AuthenticatedRequest } from '@/lib/middleware';
import { UserRole } from '@prisma/client';

async function handler(req: AuthenticatedRequest, res: NextApiResponse) {
  if (req.user!.role !== UserRole.LOGISTICS_PARTNER) {
    return res.status(403).json({
      error: 'Only logistics partners can manage capabilities',
    });
  }

  if (req.method === 'GET') {
    try {
      const capabilities = await prisma.partnerCapability.findUnique({
        where: { userId: req.user!.userId },
      });

      res.status(200).json({ capabilities });
    } catch (error) {
      console.error('Error fetching capabilities:', error);
      res.status(500).json({ error: 'Failed to fetch capabilities' });
    }
  } else if (req.method === 'PUT') {
    try {
      const {
        dgClasses,
        serviceStates,
        fleetTypes,
        packagingCapabilities,
        temperatureControlled,
        subscriptionTier,
      } = req.body;

      const capabilities = await prisma.partnerCapability.upsert({
        where: { userId: req.user!.userId },
        update: {
          dgClasses: dgClasses || [],
          serviceStates: serviceStates || [],
          fleetTypes: fleetTypes || [],
          packagingCapabilities: packagingCapabilities || [],
          temperatureControlled: temperatureControlled || false,
          subscriptionTier: subscriptionTier || 'FREE',
        },
        create: {
          userId: req.user!.userId,
          serviceTypes: [],
          dgClasses: dgClasses || [],
          productCategories: [],
          serviceCities: [],
          serviceStates: serviceStates || [],
          serviceCountries: ['India'],
          fleetTypes: fleetTypes || [],
          packagingCapabilities: packagingCapabilities || [],
          temperatureControlled: temperatureControlled || false,
          certifications: [],
          warehouseLocations: [],
        },
      });

      await prisma.auditLog.create({
        data: {
          userId: req.user!.userId,
          action: 'UPDATE_CAPABILITIES',
          entity: 'PARTNER_CAPABILITY',
          entityId: capabilities.id,
          changes: req.body,
        },
      });

      res.status(200).json({
        capabilities,
        message: 'Capabilities updated successfully',
      });
    } catch (error) {
      console.error('Error updating capabilities:', error);
      res.status(500).json({ error: 'Failed to update capabilities' });
    }
  } else {
    res.status(405).json({ error: 'Method not allowed' });
  }
}

export default withAuth(handler);

pages/api/wallet/index.ts.content:
import { NextApiResponse } from 'next';
import prisma from '@/lib/prisma';
import { withAuth, AuthenticatedRequest } from '@/lib/middleware';
import { UserRole, TransactionType } from '@prisma/client';

async function handler(req: AuthenticatedRequest, res: NextApiResponse) {
  if (req.method === 'GET') {
    try {
      if (req.user!.role !== UserRole.LOGISTICS_PARTNER) {
        return res.status(403).json({
          error: 'Only logistics partners can access wallet',
        });
      }

      const wallet = await prisma.leadWallet.findUnique({
        where: { userId: req.user!.userId },
        include: {
          transactions: {
            orderBy: { createdAt: 'desc' },
            take: 50,
          },
        },
      });

      if (!wallet) {
        return res.status(404).json({ error: 'Wallet not found' });
      }

      res.status(200).json({ wallet });
    } catch (error) {
      console.error('Error fetching wallet:', error);
      res.status(500).json({ error: 'Failed to fetch wallet' });
    }
  } else if (req.method === 'POST') {
    try {
      if (req.user!.role !== UserRole.LOGISTICS_PARTNER) {
        return res.status(403).json({
          error: 'Only logistics partners can recharge wallet',
        });
      }

      const { amount, paymentMethod } = req.body;

      if (!amount || typeof amount !== 'number' || amount <= 0 || amount > 1000000) {
        return res.status(400).json({ error: 'Invalid amount (must be between 0 and 1,000,000)' });
      }

      let wallet = await prisma.leadWallet.findUnique({
        where: { userId: req.user!.userId },
      });

      if (!wallet) {
        wallet = await prisma.leadWallet.create({
          data: {
            userId: req.user!.userId,
            balance: 0,
          },
        });
      }

      const updatedWallet = await prisma.leadWallet.update({
        where: { id: wallet.id },
        data: {
          balance: {
            increment: amount,
          },
        },
      });

      await prisma.leadTransaction.create({
        data: {
          walletId: wallet.id,
          transactionType: TransactionType.RECHARGE,
          amount,
          description: `Wallet recharge via ${paymentMethod || 'manual'}`,
        },
      });

      await prisma.auditLog.create({
        data: {
          userId: req.user!.userId,
          action: 'RECHARGE_WALLET',
          entity: 'WALLET',
          entityId: wallet.id,
          changes: { amount, newBalance: updatedWallet.balance },
        },
      });

      res.status(200).json({
        wallet: updatedWallet,
        message: 'Wallet recharged successfully',
      });
    } catch (error) {
      console.error('Error recharging wallet:', error);
      res.status(500).json({ error: 'Failed to recharge wallet' });
    }
  } else {
    res.status(405).json({ error: 'Method not allowed' });
  }
}

export default withAuth(handler);

pages/api/offers/index.ts.content:
import { NextApiResponse } from 'next';
import prisma from '@/lib/prisma';
import { withAuth, AuthenticatedRequest } from '@/lib/middleware';
import { UserRole, OfferStatus } from '@prisma/client';

async function handler(req: AuthenticatedRequest, res: NextApiResponse) {
  if (req.method === 'GET') {
    try {
      const { quoteId, status } = req.query;

      const where: any = {};

      if (req.user!.role === UserRole.LOGISTICS_PARTNER) {
        where.partnerId = req.user!.userId;
      }

      if (quoteId && typeof quoteId === 'string') {
        where.quoteId = quoteId;
      }

      if (status && typeof status === 'string') {
        where.status = status as OfferStatus;
      }

      const offers = await prisma.offer.findMany({
        where,
        include: {
          quote: {
            select: {
              id: true,
              quoteNumber: true,
              cargoName: true,
              quantity: true,
              quantityUnit: true,
              pickupCity: true,
              deliveryCity: true,
              cargoReadyDate: true,
            },
          },
          partner: {
            select: {
              id: true,
              email: true,
              companyName: true,
            },
          },
        },
        orderBy: {
          createdAt: 'desc',
        },
      });

      res.status(200).json({ offers });
    } catch (error) {
      console.error('Error fetching offers:', error);
      res.status(500).json({ error: 'Failed to fetch offers' });
    }
  } else if (req.method === 'POST') {
    try {
      if (req.user!.role !== UserRole.LOGISTICS_PARTNER) {
        return res.status(403).json({
          error: 'Only logistics partners can submit offers',
        });
      }

      const {
        quoteId,
        price,
        transitDays,
        offerValidUntil,
        pickupAvailableFrom,
        insuranceIncluded,
        trackingIncluded,
        customsClearance,
        valueAddedServices,
        termsAndConditions,
        remarks,
      } = req.body;

      if (!quoteId || !price || !transitDays || !offerValidUntil || !pickupAvailableFrom) {
        return res.status(400).json({
          error: 'Missing required fields',
        });
      }

      const quote = await prisma.quote.findUnique({
        where: { id: quoteId },
      });

      if (!quote) {
        return res.status(404).json({ error: 'Quote not found' });
      }

      const existingOffer = await prisma.offer.findFirst({
        where: {
          quoteId,
          partnerId: req.user!.userId,
          status: { not: OfferStatus.WITHDRAWN },
        },
      });

      if (existingOffer) {
        return res.status(409).json({
          error: 'You have already submitted an offer for this quote',
        });
      }

      const wallet = await prisma.leadWallet.findUnique({
        where: { userId: req.user!.userId },
      });

      if (!wallet || wallet.balance <= 0) {
        return res.status(400).json({
          error: 'Insufficient lead wallet balance',
        });
      }

      const expiresAt = new Date(offerValidUntil);

      const offer = await prisma.offer.create({
        data: {
          quoteId,
          partnerId: req.user!.userId,
          price,
          transitDays,
          offerValidUntil: new Date(offerValidUntil),
          pickupAvailableFrom: new Date(pickupAvailableFrom),
          insuranceIncluded: insuranceIncluded || false,
          trackingIncluded: trackingIncluded !== false,
          customsClearance: customsClearance || false,
          valueAddedServices: valueAddedServices || [],
          termsAndConditions,
          remarks,
          expiresAt,
        },
        include: {
          quote: true,
          partner: {
            select: {
              id: true,
              email: true,
              companyName: true,
            },
          },
        },
      });

      await prisma.auditLog.create({
        data: {
          userId: req.user!.userId,
          action: 'CREATE',
          entity: 'OFFER',
          entityId: offer.id,
          changes: { quoteId, price },
        },
      });

      res.status(201).json({
        offer,
        message: 'Offer submitted successfully',
      });
    } catch (error) {
      console.error('Error creating offer:', error);
      res.status(500).json({ error: 'Failed to create offer' });
    }
  } else {
    res.status(405).json({ error: 'Method not allowed' });
  }
}

export default withAuth(handler);

pages/admin/dashboard.tsx.content:
import React, { useEffect, useState } from 'react';
import { Layout } from '@/components/layout/Layout';
import { useAuth } from '@/contexts/AuthContext';
import Card from '@/components/ui/Card';
import Link from 'next/link';
import { useRouter } from 'next/router';

export default function AdminDashboard() {
  const { user, token } = useAuth();
  const router = useRouter();
  const [stats, setStats] = useState({
    totalQuotes: 0,
    totalOffers: 0,
    totalShipments: 0,
    activePartners: 0,
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!user || user.role !== 'ADMIN') {
      router.push('/');
      return;
    }

    fetchAdminStats();
  }, [user]);

  const fetchAdminStats = async () => {
    try {
      const response = await fetch('/api/admin/stats', {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.ok) {
        const data = await response.json();
        setStats({
          totalQuotes: data.totalQuotes || 0,
          totalOffers: data.totalOffers || 0,
          totalShipments: data.totalShipments || 0,
          activePartners: data.activePartners || 0,
        });
      }
    } catch (error) {
      console.error('Error fetching admin stats:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <Layout>
        <div className="text-center py-12">Loading...</div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div>
        <h1 className="text-3xl font-bold text-gray-900 mb-8">Admin Dashboard</h1>

        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <Card>
            <h3 className="text-sm font-medium text-gray-500 mb-2">Total Quotes</h3>
            <p className="text-3xl font-bold text-blue-600">{stats.totalQuotes}</p>
          </Card>

          <Card>
            <h3 className="text-sm font-medium text-gray-500 mb-2">Total Offers</h3>
            <p className="text-3xl font-bold text-blue-600">{stats.totalOffers}</p>
          </Card>

          <Card>
            <h3 className="text-sm font-medium text-gray-500 mb-2">Active Shipments</h3>
            <p className="text-3xl font-bold text-green-600">{stats.totalShipments}</p>
