components/forms/CheckboxGroup.tsx.content:
import React from 'react';

interface CheckboxOption {
  label: string;
  value: string;
  description?: string;
}

interface CheckboxGroupProps {
  label: string;
  name: string;
  options: CheckboxOption[];
  value: string[];
  onChange: (values: string[]) => void;
  error?: string;
  required?: boolean;
}

export function CheckboxGroup({
  label,
  name,
  options,
  value,
  onChange,
  error,
  required = false,
}: CheckboxGroupProps) {
  const handleChange = (optionValue: string, checked: boolean) => {
    if (checked) {
      onChange([...value, optionValue]);
    } else {
      onChange(value.filter((v) => v !== optionValue));
    }
  };

  return (
    <div className="mb-4">
      <label className="block text-sm font-medium text-gray-700 mb-2">
        {label}
        {required && <span className="text-red-500 ml-1">*</span>}
      </label>
      <div className="space-y-2">
        {options.map((option) => (
          <div key={option.value} className="flex items-start">
            <input
              type="checkbox"
              id={`${name}-${option.value}`}
              name={name}
              value={option.value}
              checked={value.includes(option.value)}
              onChange={(e) => handleChange(option.value, e.target.checked)}
              className="mt-1 h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
            />
            <label
              htmlFor={`${name}-${option.value}`}
              className="ml-3 cursor-pointer"
            >
              <span className="block text-sm text-gray-900">{option.label}</span>
              {option.description && (
                <span className="block text-xs text-gray-500">{option.description}</span>
              )}
            </label>
          </div>
        ))}
      </div>
      {error && (
        <p className="mt-1 text-sm text-red-600">{error}</p>
      )}
    </div>
  );
}

pages/api/documents/[id]/download.ts.content:
import { NextApiRequest, NextApiResponse } from 'next';
import { withAuth, AuthenticatedRequest } from '@/lib/middleware';
import prisma from '@/lib/prisma';
import { decryptBuffer } from '@/lib/file-encryption';
import fs from 'fs';
import path from 'path';

async function handler(req: AuthenticatedRequest, res: NextApiResponse) {
  if (req.method !== 'GET') {
    return res.status(405).json({ error: 'Method not allowed' });
  }

  try {
    const { id } = req.query;

    if (typeof id !== 'string') {
      return res.status(400).json({ error: 'Invalid document ID' });
    }

    const document = await prisma.document.findUnique({
      where: { id },
      include: {
        quote: {
          select: {
            traderId: true,
          },
        },
        shipment: {
          select: {
            offer: {
              select: {
                partnerId: true,
              },
            },
          },
        },
      },
    });

    if (!document) {
      return res.status(404).json({ error: 'Document not found' });
    }

    const isTrader = document.quote?.traderId === req.user!.userId;
    const isPartner = document.shipment?.offer.partnerId === req.user!.userId;
    const isAdmin = req.user!.role === 'ADMIN';

    if (!isTrader && !isPartner && !isAdmin) {
      return res.status(403).json({ error: 'Access denied' });
    }

    if (!document.encryptionKey) {
      return res.status(500).json({ error: 'Document encryption key not found' });
    }

    const fileUrl = document.fileUrl.startsWith('/') 
      ? document.fileUrl.substring(1) 
      : document.fileUrl;
    const encryptedFilePath = path.join(process.cwd(), fileUrl);

    if (!fs.existsSync(encryptedFilePath)) {
      return res.status(404).json({ error: 'File not found on server' });
    }

    const encryptedBuffer = await fs.promises.readFile(encryptedFilePath);

    const decryptedBuffer = await decryptBuffer(
      encryptedBuffer,
      document.encryptionKey
    );

    await prisma.auditLog.create({
      data: {
        userId: req.user!.userId,
        quoteId: document.quoteId || undefined,
        shipmentId: document.shipmentId || undefined,
        action: 'DOWNLOAD_DOCUMENT',
        entity: 'DOCUMENT',
        entityId: document.id,
      },
    });

    res.setHeader('Content-Type', document.fileType);
    res.setHeader(
      'Content-Disposition',
      `attachment; filename="${document.fileName}"`
    );
    res.setHeader('Content-Length', decryptedBuffer.length);

    res.send(decryptedBuffer);
  } catch (error) {
    console.error('Error downloading document:', error);
    res.status(500).json({ error: 'Failed to download document' });
  }
}

export default withAuth(handler);

pages/api/documents/upload.ts.content:
import { NextApiRequest, NextApiResponse } from 'next';
import { withAuth, AuthenticatedRequest } from '@/lib/middleware';
import prisma from '@/lib/prisma';
import { encryptBuffer, generateEncryptionKey } from '@/lib/file-encryption';
import formidable from 'formidable';
import fs from 'fs';
import path from 'path';

export const config = {
  api: {
    bodyParser: false,
  },
};

async function handler(req: AuthenticatedRequest, res: NextApiResponse) {
  if (req.method !== 'POST') {
    return res.status(405).json({ error: 'Method not allowed' });
  }

  try {
    const form = formidable({ multiples: false });

    const [fields, files] = await new Promise<[formidable.Fields, formidable.Files]>(
      (resolve, reject) => {
        form.parse(req, (err, fields, files) => {
          if (err) reject(err);
          else resolve([fields, files]);
        });
      }
    );

    const file = Array.isArray(files.file) ? files.file[0] : files.file;
    const quoteId = Array.isArray(fields.quoteId) ? fields.quoteId[0] : fields.quoteId;
    const documentType = Array.isArray(fields.documentType) 
      ? fields.documentType[0] 
      : fields.documentType || 'MSDS';

    if (!file) {
      return res.status(400).json({ error: 'No file uploaded' });
    }

    if (!quoteId) {
      return res.status(400).json({ error: 'Quote ID is required' });
    }

    const quote = await prisma.quote.findUnique({
      where: { id: quoteId },
    });

    if (!quote) {
      return res.status(404).json({ error: 'Quote not found' });
    }

    if (quote.traderId !== req.user!.userId) {
      return res.status(403).json({ error: 'Access denied' });
    }

    const fileBuffer = await fs.promises.readFile(file.filepath);

    const encryptionKey = generateEncryptionKey();
    const { encrypted } = await encryptBuffer(fileBuffer, encryptionKey);

    const uploadsDir = path.join(process.cwd(), 'uploads', 'encrypted');
    await fs.promises.mkdir(uploadsDir, { recursive: true });

    const encryptedFileName = `${Date.now()}-${Math.random().toString(36).substr(2, 9)}.enc`;
    const encryptedFilePath = path.join(uploadsDir, encryptedFileName);

    await fs.promises.writeFile(encryptedFilePath, encrypted);

    await fs.promises.unlink(file.filepath);

    const document = await prisma.document.create({
      data: {
        quoteId,
        fileName: file.originalFilename || 'unknown',
        fileType: file.mimetype || 'application/octet-stream',
        fileSize: file.size,
        fileUrl: `/uploads/encrypted/${encryptedFileName}`,
        encryptionKey,
        documentType,
        uploadedBy: req.user!.userId,
      },
    });

    await prisma.auditLog.create({
      data: {
        userId: req.user!.userId,
        quoteId,
        action: 'UPLOAD_DOCUMENT',
        entity: 'DOCUMENT',
        entityId: document.id,
        changes: {
          fileName: document.fileName,
          documentType,
        },
      },
    });

    res.status(201).json({
      document: {
        id: document.id,
        fileName: document.fileName,
        fileType: document.fileType,
        fileSize: document.fileSize,
        documentType: document.documentType,
        createdAt: document.createdAt,
      },
      message: 'Document uploaded and encrypted successfully',
    });
  } catch (error) {
    console.error('Error uploading document:', error);
    res.status(500).json({ error: 'Failed to upload document' });
  }
}

export default withAuth(handler);

pages/partner/submit-offer.tsx.content:
import React, { useState, useEffect } from 'react';
import { useRouter } from 'next/router';
import { Layout } from '@/components/layout/Layout';
import { Card, CardHeader, CardBody, CardTitle } from '@/components/ui/Card';
import { FormField } from '@/components/forms/FormField';
import Button from '@/components/ui/Button';
import { CountdownTimer } from '@/components/CountdownTimer';
import { useAuth } from '@/contexts/AuthContext';

export default function SubmitOffer() {
  const { user, token } = useAuth();
  const router = useRouter();
  const { quoteId } = router.query;
  const [quote, setQuote] = useState<any>(null);
  const [wallet, setWallet] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [estimatedCost, setEstimatedCost] = useState(0);

  const [formData, setFormData] = useState({
    price: '',
    transitDays: '',
    offerValidUntil: '',
    pickupAvailableFrom: '',
    insuranceIncluded: false,
    trackingIncluded: true,
    customsClearance: false,
    valueAddedServices: '',
    termsAndConditions: '',
    remarks: '',
  });

  useEffect(() => {
    if (token && quoteId) {
      fetchQuote();
      fetchWallet();
    }
  }, [token, quoteId]);

  const fetchQuote = async () => {
    try {
      const response = await fetch(`/api/quotes/${quoteId}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      const data = await response.json();
      if (response.ok) {
        setQuote(data.quote);
        calculateEstimatedCost(data.quote);
      }
    } catch (error) {
      console.error('Error fetching quote:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchWallet = async () => {
    try {
      const response = await fetch('/api/wallet', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      const data = await response.json();
      if (response.ok) {
        setWallet(data.wallet);
      }
    } catch (error) {
      console.error('Error fetching wallet:', error);
    }
  };

  const calculateEstimatedCost = (quoteData: any) => {
    let cost = 500;
    if (quoteData.isHazardous) cost += 300;
    if (quoteData.quantity > 50) cost += 200;
    setEstimatedCost(cost);
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value, type } = e.target;
    const checked = (e.target as HTMLInputElement).checked;

    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!quoteId) return;

    if (wallet && wallet.balance < estimatedCost) {
      alert(`Insufficient balance. You need ${estimatedCost} to submit this offer. Current balance: ${wallet.balance}`);
      return;
    }

    setSubmitting(true);

    try {
      const response = await fetch('/api/offers', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({
          quoteId,
          ...formData,
          price: parseFloat(formData.price),
          transitDays: parseInt(formData.transitDays),
          valueAddedServices: formData.valueAddedServices.split(',').map(s => s.trim()).filter(Boolean),
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to submit offer');
      }

      router.push('/partner/dashboard');
    } catch (error) {
      alert(error instanceof Error ? error.message : 'Failed to submit offer');
    } finally {
      setSubmitting(false);
    }
  };

  if (!user || user.role !== 'LOGISTICS_PARTNER') {
    return (
      <Layout>
        <div className="text-center py-12">
          <p className="text-gray-600">Access denied. Partners only.</p>
        </div>
      </Layout>
    );
  }

  if (loading) {
    return (
      <Layout>
        <div className="text-center py-12">
          <div className="inline-block animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
        </div>
      </Layout>
    );
  }

  if (!quote) {
    return (
      <Layout>
        <div className="text-center py-12">
          <p className="text-gray-600">Quote not found</p>
        </div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="max-w-5xl mx-auto">
        <div className="flex items-center justify-between mb-6">
          <h1 className="text-3xl font-bold text-gray-900">Submit Offer</h1>
          {quote.expiresAt && <CountdownTimer expiresAt={quote.expiresAt} />}
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          <div className="lg:col-span-2">
            <Card>
              <CardHeader>
                <CardTitle>Offer Details</CardTitle>
              </CardHeader>
              <CardBody>
                <form onSubmit={handleSubmit} className="space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <FormField
                      label="Your Price (INR)"
                      name="price"
                      type="number"
                      value={formData.price}
                      onChange={handleChange}
                      required
                      min="0"
                      step="0.01"
                      placeholder="Enter your competitive price"
                    />
                    <FormField
                      label="Transit Days"
                      name="transitDays"
                      type="number"
                      value={formData.transitDays}
                      onChange={handleChange}
                      required
                      min="1"
                      placeholder="Number of days"
                    />
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <FormField
                      label="Offer Valid Until"
                      name="offerValidUntil"
                      type="datetime-local"
                      value={formData.offerValidUntil}
                      onChange={handleChange}
                      required
                    />
                    <FormField
                      label="Pickup Available From"
                      name="pickupAvailableFrom"
                      type="datetime-local"
                      value={formData.pickupAvailableFrom}
                      onChange={handleChange}
                      required
                    />
                  </div>

                  <div className="space-y-2">
                    <label className="block text-sm font-medium text-gray-700">
                      Value-Added Services
                    </label>
                    <div className="space-y-2">
                      <div className="flex items-center">
                        <input
                          type="checkbox"
                          id="insuranceIncluded"
                          name="insuranceIncluded"
                          checked={formData.insuranceIncluded}
                          onChange={handleChange}
                          className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                        />
                        <label htmlFor="insuranceIncluded" className="ml-2 text-sm text-gray-700">
                          Insurance Included
                        </label>
                      </div>
                      <div className="flex items-center">
                        <input
                          type="checkbox"
                          id="trackingIncluded"
                          name="trackingIncluded"
                          checked={formData.trackingIncluded}
                          onChange={handleChange}
                          className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                        />
                        <label htmlFor="trackingIncluded" className="ml-2 text-sm text-gray-700">
                          Real-time Tracking Included
                        </label>
                      </div>
                      <div className="flex items-center">
                        <input
                          type="checkbox"
                          id="customsClearance"
                          name="customsClearance"
                          checked={formData.customsClearance}
                          onChange={handleChange}
                          className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                        />
                        <label htmlFor="customsClearance" className="ml-2 text-sm text-gray-700">
                          Customs Clearance Assistance
                        </label>
                      </div>
                    </div>
                  </div>

                  <FormField
                    label="Additional Services (comma-separated)"
                    name="valueAddedServices"
                    type="textarea"
                    value={formData.valueAddedServices}
                    onChange={handleChange}
                    placeholder="e.g., Warehousing, Packing, Loading assistance"
                  />

                  <FormField
                    label="Terms & Conditions"
                    name="termsAndConditions"
                    type="textarea"
                    value={formData.termsAndConditions}
                    onChange={handleChange}
                    placeholder="Your terms and conditions"
                  />

                  <FormField
                    label="Remarks"
                    name="remarks"
                    type="textarea"
                    value={formData.remarks}
                    onChange={handleChange}
                    placeholder="Any additional information"
                  />

                  <div className="flex justify-end space-x-3 pt-4">
                    <Button
                      type="button"
                      variant="secondary"
                      onClick={() => router.back()}
                    >
                      Cancel
                    </Button>
                    <Button
                      type="submit"
                      variant="primary"
                      disabled={submitting}
                    >
                      {submitting ? 'Submitting...' : 'Submit Offer'}
                    </Button>
                  </div>
                </form>
              </CardBody>
            </Card>
          </div>

          <div className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle>Quote Summary</CardTitle>
              </CardHeader>
              <CardBody>
                <div className="space-y-3">
                  <div>
                    <p className="text-sm text-gray-500">Cargo</p>
                    <p className="font-medium">{quote.cargoName}</p>
                  </div>
                  <div>
                    <p className="text-sm text-gray-500">Quantity</p>
                    <p className="font-medium">{quote.quantity} {quote.quantityUnit}</p>
                  </div>
                  <div>
                    <p className="text-sm text-gray-500">From</p>
                    <p className="font-medium">{quote.pickupCity}, {quote.pickupState}</p>
                  </div>
                  <div>
                    <p className="text-sm text-gray-500">To</p>
                    <p className="font-medium">{quote.deliveryCity}, {quote.deliveryState}</p>
                  </div>
                  <div>
                    <p className="text-sm text-gray-500">Ready Date</p>
                    <p className="font-medium">{new Date(quote.cargoReadyDate).toLocaleDateString()}</p>
                  </div>
                  {quote.isHazardous && (
                    <div className="pt-2 border-t">
                      <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800">
                         Hazardous: {quote.hazardClass}
                      </span>
                    </div>
                  )}
                </div>
              </CardBody>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle>Estimated Lead Cost</CardTitle>
              </CardHeader>
              <CardBody>
                <div className="text-center">
                  <p className="text-3xl font-bold text-blue-600 mb-2">
                    {estimatedCost}
                  </p>
                  <p className="text-sm text-gray-600 mb-4">
                    Will be deducted if your offer is selected
                  </p>
                  {wallet && (
                    <div className="pt-4 border-t">
                      <p className="text-sm text-gray-500">Current Balance</p>
                      <p className={`text-xl font-semibold ${wallet.balance >= estimatedCost ? 'text-green-600' : 'text-red-600'}`}>
                        {wallet.balance.toLocaleString()}
                      </p>
                      {wallet.balance < estimatedCost && (
                        <p className="text-xs text-red-600 mt-2">
                          Insufficient balance
                        </p>
                      )}
                    </div>
                  )}
                </div>
              </CardBody>
            </Card>
          </div>
        </div>
      </div>
    </Layout>
  );
}

