      message: 'Only 10 minutes remaining to submit your offer',
      priority: 'URGENT',
      timestamp: new Date(Date.now() - 600000).toISOString(),
      read: false,
      type: 'TIMER_WARNING',
    },
    {
      id: '3',
      title: 'Low Wallet Balance',
      message: 'Your current balance is 450. Please recharge soon.',
      priority: 'MEDIUM',
      timestamp: new Date(Date.now() - 3600000).toISOString(),
      read: true,
      type: 'LOW_BALANCE',
    },
  ]);

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'URGENT':
        return 'bg-red-100 text-red-800 border-red-300';
      case 'HIGH':
        return 'bg-orange-100 text-orange-800 border-orange-300';
      case 'MEDIUM':
        return 'bg-yellow-100 text-yellow-800 border-yellow-300';
      default:
        return 'bg-blue-100 text-blue-800 border-blue-300';
    }
  };

  const getTimeAgo = (timestamp: string) => {
    const now = new Date().getTime();
    const then = new Date(timestamp).getTime();
    const diff = now - then;
    const minutes = Math.floor(diff / 60000);

    if (minutes < 1) return 'Just now';
    if (minutes < 60) return `${minutes}m ago`;
    const hours = Math.floor(minutes / 60);
    if (hours < 24) return `${hours}h ago`;
    const days = Math.floor(hours / 24);
    return `${days}d ago`;
  };

  const unreadCount = notifications.filter(n => !n.read).length;

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <h3 className="text-lg font-semibold text-gray-900">
            Notifications
          </h3>
          {unreadCount > 0 && (
            <span className="inline-flex items-center justify-center px-2 py-1 text-xs font-bold leading-none text-white bg-red-600 rounded-full">
              {unreadCount}
            </span>
          )}
        </div>
      </CardHeader>
      <CardBody className="p-0">
        <div className="divide-y divide-gray-200">
          {notifications.map((notification) => (
            <div
              key={notification.id}
              className={`p-4 hover:bg-gray-50 transition-colors ${
                !notification.read ? 'bg-blue-50' : ''
              }`}
            >
              <div className="flex items-start space-x-3">
                <div className="flex-1 min-w-0">
                  <div className="flex items-center space-x-2 mb-1">
                    <p className={`text-sm font-semibold ${!notification.read ? 'text-gray-900' : 'text-gray-700'}`}>
                      {notification.title}
                    </p>
                    <span className={`px-2 py-0.5 text-xs font-semibold rounded border ${getPriorityColor(notification.priority)}`}>
                      {notification.priority}
                    </span>
                  </div>
                  <p className="text-sm text-gray-600">
                    {notification.message}
                  </p>
                  <p className="text-xs text-gray-500 mt-1">
                    {getTimeAgo(notification.timestamp)}
                  </p>
                </div>
                {!notification.read && (
                  <div className="flex-shrink-0">
                    <div className="w-2 h-2 bg-blue-600 rounded-full"></div>
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      </CardBody>
    </Card>
  );
};

lib/wallet-alerts.ts.content:
import prisma from './prisma';
import { notifyLowWalletBalance } from './notifications';

export async function checkWalletBalance(userId: string): Promise<{
  balance: number;
  alertTriggered: boolean;
  threshold: number;
}> {
  const wallet = await prisma.leadWallet.findUnique({
    where: { userId },
  });

  if (!wallet) {
    throw new Error('Wallet not found');
  }

  const alertTriggered = wallet.lowBalanceAlert && wallet.balance <= wallet.alertThreshold;

  if (alertTriggered) {
    await notifyLowWalletBalance(userId, wallet.balance, wallet.alertThreshold);

    await prisma.auditLog.create({
      data: {
        userId,
        action: 'LOW_BALANCE_ALERT',
        entity: 'WALLET',
        entityId: wallet.id,
        changes: {
          balance: wallet.balance,
          threshold: wallet.alertThreshold,
        },
      },
    });
  }

  return {
    balance: wallet.balance,
    alertTriggered,
    threshold: wallet.alertThreshold,
  };
}

export async function updateWalletAlertSettings(
  userId: string,
  settings: {
    lowBalanceAlert?: boolean;
    alertThreshold?: number;
  }
): Promise<void> {
  await prisma.leadWallet.update({
    where: { userId },
    data: settings,
  });

  await prisma.auditLog.create({
    data: {
      userId,
      action: 'UPDATE_WALLET_ALERT_SETTINGS',
      entity: 'WALLET',
      entityId: userId,
      changes: settings,
    },
  });
}

export async function checkAllWallets(): Promise<void> {
  const lowBalanceWallets = await prisma.leadWallet.findMany({
    where: {
      lowBalanceAlert: true,
      balance: {
        lte: prisma.leadWallet.fields.alertThreshold,
      },
    },
    include: {
      user: {
        select: {
          id: true,
          email: true,
          companyName: true,
        },
      },
    },
  });

  for (const wallet of lowBalanceWallets) {
    await notifyLowWalletBalance(
      wallet.userId,
      wallet.balance,
      wallet.alertThreshold
    );
  }
}

pages/partner/leads.tsx.content:
import React, { useState, useEffect } from 'react';
import { useRouter } from 'next/router';
import { Layout } from '@/components/layout/Layout';
import Card, { CardHeader, CardBody, CardTitle } from '@/components/ui/Card';
import { CountdownTimer } from '@/components/CountdownTimer';
import Button from '@/components/ui/Button';
import Badge from '@/components/ui/Badge';
import { useAuth } from '@/contexts/AuthContext';
import { EmptyState } from '@/components/ui/EmptyState';

interface Lead {
  id: string;
  quoteNumber: string;
  cargoName: string;
  quantity: number;
  quantityUnit: string;
  pickupCity: string;
  pickupState: string;
  deliveryCity: string;
  deliveryState: string;
  cargoReadyDate: string;
  isHazardous: boolean;
  hazardClass: string | null;
  expiresAt: string;
  status: string;
  packagingType: string;
}

export default function PartnerLeads() {
  const { user, token } = useAuth();
  const router = useRouter();
  const [leads, setLeads] = useState<Lead[]>([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState<string>('all');
  const [hazardFilter, setHazardFilter] = useState<string>('all');
  const [searchQuery, setSearchQuery] = useState<string>('');
  const [showFilters, setShowFilters] = useState(false);

  useEffect(() => {
    if (token) {
      fetchLeads();
    }
  }, [token, filter]);

  const fetchLeads = async () => {
    try {
      const url = filter === 'all' ? '/api/quotes' : `/api/quotes?status=${filter}`;
      const response = await fetch(url, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      const data = await response.json();
      if (response.ok) {
        setLeads(data.quotes || []);
      }
    } catch (error) {
      console.error('Error fetching leads:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredLeads = leads.filter(lead => {
    // Hazard filter
    if (hazardFilter === 'hazardous' && !lead.isHazardous) return false;
    if (hazardFilter === 'non-hazardous' && lead.isHazardous) return false;

    // Search filter
    if (searchQuery) {
      const query = searchQuery.toLowerCase();
      return (
        lead.cargoName.toLowerCase().includes(query) ||
        lead.quoteNumber.toLowerCase().includes(query) ||
        lead.pickupCity.toLowerCase().includes(query) ||
        lead.deliveryCity.toLowerCase().includes(query) ||
        lead.pickupState.toLowerCase().includes(query) ||
        lead.deliveryState.toLowerCase().includes(query)
      );
    }

    return true;
  });

  const handleSubmitOffer = (leadId: string) => {
    router.push(`/partner/submit-offer?quoteId=${leadId}`);
  };

  if (!user || user.role !== 'LOGISTICS_PARTNER') {
    return (
      <Layout>
        <div className="text-center py-12">
          <p className="text-gray-600">Access denied. Partners only.</p>
        </div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="max-w-7xl mx-auto">
        <div className="mb-6">
          <div className="flex items-center justify-between mb-4">
            <h1 className="text-3xl font-bold text-gray-900">Available Leads</h1>
            <Button
              variant="outline"
              onClick={() => setShowFilters(!showFilters)}
              className="flex items-center space-x-2"
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 4a1 1 0 011-1h16a1 1 0 011 1v2.586a1 1 0 01-.293.707l-6.414 6.414a1 1 0 00-.293.707V17l-4 4v-6.586a1 1 0 00-.293-.707L3.293 7.293A1 1 0 013 6.586V4z" />
              </svg>
              <span>{showFilters ? 'Hide' : 'Show'} Filters</span>
            </Button>
          </div>

          {showFilters && (
            <Card className="mb-4">
              <CardBody>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Status
                    </label>
                    <select
                      value={filter}
                      onChange={(e) => setFilter(e.target.value)}
                      className="w-full px-4 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
                    >
                      <option value="all">All Statuses</option>
                      <option value="MATCHING">Active</option>
                      <option value="OFFERS_AVAILABLE">With Offers</option>
                      <option value="EXPIRED">Expired</option>
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Hazard Type
                    </label>
                    <select
                      value={hazardFilter}
                      onChange={(e) => setHazardFilter(e.target.value)}
                      className="w-full px-4 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
                    >
                      <option value="all">All Types</option>
                      <option value="hazardous">Hazardous Only</option>
                      <option value="non-hazardous">Non-Hazardous Only</option>
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Search
                    </label>
                    <input
                      type="text"
                      placeholder="Search cargo, location, or quote ID..."
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                      className="w-full px-4 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
                    />
                  </div>
                </div>

                {(hazardFilter !== 'all' || searchQuery) && (
                  <div className="mt-4 flex items-center justify-between">
                    <p className="text-sm text-gray-600">
                      Showing {filteredLeads.length} of {leads.length} leads
                    </p>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => {
                        setHazardFilter('all');
                        setSearchQuery('');
                      }}
                    >
                      Clear Filters
                    </Button>
                  </div>
                )}
              </CardBody>
            </Card>
          )}
        </div>

        {loading ? (
          <div className="text-center py-12">
            <div className="inline-block animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
          </div>
        ) : filteredLeads.length === 0 ? (
          <Card>
            <CardBody className="text-center py-12">
              <EmptyState
                icon=""
                title={searchQuery || hazardFilter !== 'all' ? "No matching leads found" : "No leads available"}
                description={searchQuery || hazardFilter !== 'all' ? "Try adjusting your filters" : "New freight requests matching your capabilities will appear here"}
                actionLabel={searchQuery || hazardFilter !== 'all' ? "Clear Filters" : undefined}
                onAction={searchQuery || hazardFilter !== 'all' ? () => {
                  setSearchQuery('');
                  setHazardFilter('all');
                } : undefined}
              />
            </CardBody>
          </Card>
        ) : (
          <div className="grid gap-4">
            {filteredLeads.map((lead) => (
              <Card key={lead.id} className="hover:shadow-md transition-shadow">
                <CardBody>
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <div className="flex items-center space-x-3 mb-3">
                        <h3 className="text-xl font-semibold text-gray-900">
                          {lead.cargoName}
                        </h3>
                        {lead.isHazardous && (
                          <Badge variant="danger">
                             Hazardous {lead.hazardClass}
                          </Badge>
                        )}
                        <Badge variant={lead.status === 'MATCHING' ? 'success' : 'neutral'}>
                          {lead.status}
                        </Badge>
                      </div>

                      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-4">
                        <div>
                          <p className="text-sm text-gray-500">Quote ID</p>
                          <p className="font-medium text-gray-900">{lead.quoteNumber}</p>
                        </div>
                        <div>
                          <p className="text-sm text-gray-500">Quantity</p>
                          <p className="font-medium text-gray-900">
                            {lead.quantity} {lead.quantityUnit}
                          </p>
                        </div>
                        <div>
                          <p className="text-sm text-gray-500">Packaging</p>
                          <p className="font-medium text-gray-900">{lead.packagingType}</p>
                        </div>
                        <div>
                          <p className="text-sm text-gray-500">Ready Date</p>
                          <p className="font-medium text-gray-900">
                            {new Date(lead.cargoReadyDate).toLocaleDateString()}
                          </p>
                        </div>
                      </div>

                      <div className="flex items-center space-x-4 text-sm text-gray-600">
                        <div className="flex items-center space-x-1">
                          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                          </svg>
                          <span>{lead.pickupCity}, {lead.pickupState}</span>
                        </div>
                        <span></span>
                        <div className="flex items-center space-x-1">
                          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                          </svg>
                          <span>{lead.deliveryCity}, {lead.deliveryState}</span>
                        </div>
                      </div>
                    </div>

                    <div className="flex flex-col items-end space-y-3">
                      {lead.expiresAt && (
                        <CountdownTimer expiresAt={lead.expiresAt} />
                      )}
                      <Button
                        variant="primary"
                        onClick={() => handleSubmitOffer(lead.id)}
                        disabled={lead.status === 'EXPIRED'}
                      >
                        Submit Offer
                      </Button>
                    </div>
                  </div>
                </CardBody>
              </Card>
            ))}
          </div>
        )}
      </div>
    </Layout>
  );
}

pages/trader/dashboard.tsx.content:
import React, { useState, useEffect } from 'react';
import { Layout } from '@/components/layout/Layout';
import Card, { CardHeader, CardBody, CardTitle } from '@/components/ui/Card';
import { NotificationCenter } from '@/components/NotificationCenter';
import Button from '@/components/ui/Button';
import Badge from '@/components/ui/Badge';
import EmptyState from '@/components/ui/EmptyState';
import Link from 'next/link';
import { useRouter } from 'next/router';
import { useAuth } from '@/contexts/AuthContext';

export default function TraderDashboard() {
  const { user, token } = useAuth();
  const router = useRouter();
  const [quotes, setQuotes] = useState<any[]>([]);
  const [stats, setStats] = useState({
    active: 0,
    withOffers: 0,
    selected: 0,
    total: 0,
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (token) {
      fetchQuotes();
    }
  }, [token]);

  const fetchQuotes = async () => {
    try {
      const response = await fetch('/api/quotes', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      const data = await response.json();
      if (response.ok) {
        const quotesList = data.quotes || [];
        setQuotes(quotesList);

        setStats({
          active: quotesList.filter((q: any) => q.status === 'MATCHING').length,
          withOffers: quotesList.filter((q: any) => q.status === 'OFFERS_AVAILABLE').length,
          selected: quotesList.filter((q: any) => q.status === 'SELECTED').length,
          total: quotesList.length,
        });
      }
    } catch (error) {
      console.error('Error fetching quotes:', error);
    } finally {
      setLoading(false);
    }
  };

  if (!user || user.role !== 'TRADER') {
    return (
      <Layout>
        <div className="text-center py-12">
          <p className="text-gray-600">Access denied. Traders only.</p>
        </div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="max-w-7xl mx-auto">
        <div className="flex items-center justify-between mb-6">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Trader Dashboard</h1>
            <p className="text-gray-600 mt-1">Welcome back, {user.companyName || user.email}</p>
          </div>
          <Link href="/quotes/new">
            <Button variant="primary" className="flex items-center space-x-2">
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
              </svg>
              <span>New Freight Request</span>
            </Button>
          </Link>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-6">
          <Card>
            <CardBody className="text-center">
              <p className="text-sm text-gray-600 mb-1">Active Quotes</p>
              <p className="text-4xl font-bold text-blue-600">{stats.active}</p>
            </CardBody>
          </Card>
          <Card>
            <CardBody className="text-center">
              <p className="text-sm text-gray-600 mb-1">With Offers</p>
              <p className="text-4xl font-bold text-green-600">{stats.withOffers}</p>
            </CardBody>
          </Card>
          <Card>
            <CardBody className="text-center">
              <p className="text-sm text-gray-600 mb-1">Selected</p>
              <p className="text-4xl font-bold text-purple-600">{stats.selected}</p>
            </CardBody>
          </Card>
          <Card>
            <CardBody className="text-center">
              <p className="text-sm text-gray-600 mb-1">Total Quotes</p>
              <p className="text-4xl font-bold text-gray-900">{stats.total}</p>
            </CardBody>
          </Card>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          <div className="lg:col-span-2">
            <Card>
              <CardHeader>
                <CardTitle>Recent Freight Requests</CardTitle>
              </CardHeader>
              <CardBody className="p-0">
                {loading ? (
                  <div className="text-center py-12">
                    <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
                  </div>
                ) : quotes.length === 0 ? (
                  <EmptyState
                    icon=""
                    title="No Freight Requests Yet"
                    description="Create your first request to receive competitive quotations from pre-verified logistics partners."
                    actionLabel="+ Create Your First Quote"
                    actionHref="/quotes/new"
                  />
                ) : (
                  <div className="divide-y divide-gray-200">
                    {quotes.slice(0, 5).map((quote) => (
                      <div
                        key={quote.id}
                        className="p-4 hover:bg-gray-50 cursor-pointer transition-colors"
                        onClick={() => router.push(`/quotes/${quote.id}`)}
                      >
                        <div className="flex items-start justify-between">
                          <div className="flex-1">
                            <div className="flex items-center space-x-2 mb-2">
                              <h4 className="font-semibold text-gray-900">{quote.cargoName}</h4>
                              <Badge variant={
                                quote.status === 'SELECTED' ? 'success' :
                                quote.status === 'OFFERS_AVAILABLE' ? 'primary' :
                                quote.status === 'MATCHING' ? 'warning' :
                                'neutral'
                              }>
                                {quote.status}
                              </Badge>
                            </div>
                            <p className="text-sm text-gray-600">
                              {quote.pickupCity}  {quote.deliveryCity} | {quote.quantity} {quote.quantityUnit}
                            </p>
                            <p className="text-xs text-gray-500 mt-1">
                              {quote.quoteNumber} | {quote.offers?.length || 0} offers
                            </p>
                          </div>
                          <div>
                            <svg className="w-5 h-5 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                            </svg>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
                {quotes.length > 5 && (
                  <div className="p-4 border-t">
                    <Link href="/quotes">
                      <Button variant="secondary" className="w-full">
                        View All Requests
                      </Button>
                    </Link>
                  </div>
                )}
              </CardBody>
            </Card>
          </div>

          <div>
            <NotificationCenter />
          </div>
        </div>
      </div>
    </Layout>
  );
}

lib/quote-timer.ts.content:
import prisma from './prisma';
import { QuoteStatus, OfferStatus } from '@prisma/client';
import { notifyOfferExpiringSoon } from './notifications';

const DEFAULT_QUOTE_TIMER_MINUTES = 60;
const WARNING_THRESHOLD_MINUTES = 10;

export interface QuoteTimerConfig {
  quoteId: string;
  timerDurationMinutes?: number;
  enableWarnings?: boolean;
}

export async function startQuoteTimer(config: QuoteTimerConfig): Promise<Date> {
  const {
    quoteId,
    timerDurationMinutes = DEFAULT_QUOTE_TIMER_MINUTES,
    enableWarnings = true,
  } = config;

  const expiresAt = new Date();
  expiresAt.setMinutes(expiresAt.getMinutes() + timerDurationMinutes);

  await prisma.quote.update({
    where: { id: quoteId },
    data: {
      expiresAt,
      status: QuoteStatus.MATCHING,
    },
  });

  if (enableWarnings) {
    const warningTime = new Date();
    warningTime.setMinutes(
      warningTime.getMinutes() + timerDurationMinutes - WARNING_THRESHOLD_MINUTES
    );

    scheduleExpiryWarning(quoteId, warningTime);
  }

  scheduleQuoteExpiry(quoteId, expiresAt);

  await prisma.auditLog.create({
    data: {
      quoteId,
      action: 'QUOTE_TIMER_STARTED',
      entity: 'QUOTE',
      entityId: quoteId,
      changes: {
        timerDurationMinutes,
        expiresAt,
      },
    },
  });

  return expiresAt;
}

export async function extendQuoteTimer(
  quoteId: string,
  additionalMinutes: number
): Promise<Date> {
  const quote = await prisma.quote.findUnique({
    where: { id: quoteId },
  });

  if (!quote) {
    throw new Error('Quote not found');
  }

  if (!quote.expiresAt) {
    throw new Error('Quote does not have an expiry time');
  }

  const newExpiresAt = new Date(quote.expiresAt);
  newExpiresAt.setMinutes(newExpiresAt.getMinutes() + additionalMinutes);

  await prisma.quote.update({
    where: { id: quoteId },
    data: {
      expiresAt: newExpiresAt,
    },
  });

  await prisma.auditLog.create({
    data: {
      quoteId,
      action: 'QUOTE_TIMER_EXTENDED',
      entity: 'QUOTE',
      entityId: quoteId,
      changes: {
        additionalMinutes,
        newExpiresAt,
      },
    },
  });

  return newExpiresAt;
}

export async function getRemainingTime(quoteId: string): Promise<{
  expiresAt: Date;
  remainingMinutes: number;
  hasExpired: boolean;
}> {
  const quote = await prisma.quote.findUnique({
    where: { id: quoteId },
    select: { expiresAt: true },
  });

  if (!quote?.expiresAt) {
    throw new Error('Quote does not have an expiry time');
  }

  const now = new Date();
  const remainingMs = quote.expiresAt.getTime() - now.getTime();
  const remainingMinutes = Math.floor(remainingMs / (1000 * 60));

  return {
    expiresAt: quote.expiresAt,
    remainingMinutes: Math.max(0, remainingMinutes),
    hasExpired: remainingMs <= 0,
  };
}

async function scheduleExpiryWarning(
  quoteId: string,
  warningTime: Date
): Promise<void> {
  const delay = warningTime.getTime() - Date.now();

  if (delay > 0) {
    setTimeout(async () => {
      try {
        const quote = await prisma.quote.findUnique({
          where: { id: quoteId },
          select: {
            id: true,
            expiresAt: true,
            status: true,
          },
        });

        if (!quote || quote.status === QuoteStatus.EXPIRED) {
          return;
        }

        const offers = await prisma.offer.findMany({
          where: {
            quoteId,
            status: OfferStatus.PENDING,
          },
          select: {
            partnerId: true,
          },
        });

        const partners = await prisma.quote.findUnique({
          where: { id: quoteId },
          select: {
            offers: {
              select: {
                partnerId: true,
              },
            },
          },
        });

        if (partners?.offers) {
          for (const offer of partners.offers) {
            await notifyOfferExpiringSoon(
              quoteId,
              offer.partnerId,
              quote.expiresAt!
            );
          }
        }
      } catch (error) {
        console.error('Error sending expiry warning:', error);
      }
    }, delay);
  }
}

async function scheduleQuoteExpiry(
  quoteId: string,
  expiresAt: Date
): Promise<void> {
  const delay = expiresAt.getTime() - Date.now();

  if (delay > 0) {
    setTimeout(async () => {
      try {
        await expireQuote(quoteId);
      } catch (error) {
        console.error('Error expiring quote:', error);
      }
    }, delay);
  }
}

export async function expireQuote(quoteId: string): Promise<void> {
  const quote = await prisma.quote.findUnique({
    where: { id: quoteId },
    select: {
      id: true,
      status: true,
      expiresAt: true,
    },
  });

  if (!quote) {
    return;
  }

  if (quote.status === QuoteStatus.SELECTED || quote.status === QuoteStatus.CANCELLED) {
    return;
  }

  await prisma.quote.update({
    where: { id: quoteId },
    data: {
      status: QuoteStatus.EXPIRED,
    },
  });

  await prisma.offer.updateMany({
    where: {
      quoteId,
      status: OfferStatus.PENDING,
    },
    data: {
      status: OfferStatus.EXPIRED,
    },
  });

  await prisma.auditLog.create({
    data: {
      quoteId,
      action: 'QUOTE_EXPIRED',
      entity: 'QUOTE',
      entityId: quoteId,
      changes: {
        expiresAt: quote.expiresAt,
      },
    },
  });
}

export async function checkExpiredQuotes(): Promise<void> {
  const expiredQuotes = await prisma.quote.findMany({
    where: {
      expiresAt: {
        lte: new Date(),
      },
      status: {
        in: [QuoteStatus.MATCHING, QuoteStatus.OFFERS_AVAILABLE],
      },
    },
  });

  for (const quote of expiredQuotes) {
    await expireQuote(quote.id);
  }
}

lib/notifications.ts.content:
import prisma from './prisma';

export interface NotificationOptions {
  userId: string;
  title: string;
  message: string;
  type: 'EMAIL' | 'SMS' | 'WHATSAPP' | 'PORTAL';
  priority?: 'LOW' | 'MEDIUM' | 'HIGH' | 'URGENT';
  data?: any;
}

export interface MultiChannelNotificationOptions {
  userId: string;
  title: string;
  message: string;
  channels: ('EMAIL' | 'SMS' | 'WHATSAPP' | 'PORTAL')[];
  priority?: 'LOW' | 'MEDIUM' | 'HIGH' | 'URGENT';
  data?: any;
}

export async function sendMultiChannelNotification(
  options: MultiChannelNotificationOptions
): Promise<void> {
  const { userId, title, message, channels, priority = 'MEDIUM', data } = options;

  const user = await prisma.user.findUnique({
    where: { id: userId },
    select: { email: true, phone: true, companyName: true },
  });

  if (!user) {
    throw new Error('User not found');
  }

  const notificationPromises = channels.map((channel) =>
    sendNotification({
      userId,
      title,
      message,
      type: channel,
      priority,
      data,
    })
  );

  await Promise.allSettled(notificationPromises);
}

export async function sendNotification(
  options: NotificationOptions
): Promise<void> {
  const { userId, title, message, type, priority = 'MEDIUM', data } = options;

  switch (type) {
    case 'EMAIL':
      await sendEmailNotification(userId, title, message, data);
      break;
    case 'SMS':
      await sendSMSNotification(userId, message, data);
      break;
    case 'WHATSAPP':
      await sendWhatsAppNotification(userId, message, data);
      break;
    case 'PORTAL':
      await createPortalNotification(userId, title, message, priority, data);
      break;
  }

  await prisma.auditLog.create({
    data: {
      userId,
      action: 'NOTIFICATION_SENT',
      entity: 'NOTIFICATION',
      entityId: userId,
      changes: {
        type,
        title,
        priority,
      },
    },
  });
}

async function sendEmailNotification(
  userId: string,
  title: string,
  message: string,
  data?: any
): Promise<void> {
  const user = await prisma.user.findUnique({
    where: { id: userId },
    select: { email: true, companyName: true },
  });

  if (!user?.email) {
    console.error('User email not found');
    return;
  }

  console.log(`[EMAIL] To: ${user.email}`);
  console.log(`[EMAIL] Subject: ${title}`);
  console.log(`[EMAIL] Message: ${message}`);
  console.log(`[EMAIL] Data:`, data);
}

async function sendSMSNotification(
  userId: string,
  message: string,
  data?: any
): Promise<void> {
  const user = await prisma.user.findUnique({
    where: { id: userId },
    select: { phone: true },
  });

  if (!user?.phone) {
    console.error('User phone not found');
    return;
  }

  console.log(`[SMS] To: ${user.phone}`);
  console.log(`[SMS] Message: ${message}`);
  console.log(`[SMS] Data:`, data);
}

async function sendWhatsAppNotification(
  userId: string,
  message: string,
  data?: any
): Promise<void> {
  const user = await prisma.user.findUnique({
    where: { id: userId },
    select: { phone: true },
  });

  if (!user?.phone) {
    console.error('User phone not found');
    return;
  }

  console.log(`[WHATSAPP] To: ${user.phone}`);
  console.log(`[WHATSAPP] Message: ${message}`);
  console.log(`[WHATSAPP] Data:`, data);
}

async function createPortalNotification(
  userId: string,
  title: string,
  message: string,
  priority: string,
  data?: any
): Promise<void> {
  console.log(`[PORTAL] User: ${userId}`);
  console.log(`[PORTAL] Title: ${title}`);
  console.log(`[PORTAL] Message: ${message}`);
  console.log(`[PORTAL] Priority: ${priority}`);
  console.log(`[PORTAL] Data:`, data);
}

export async function notifyPartnerAboutNewLead(
  partnerId: string,
  quoteId: string,
  quoteDetails: any
): Promise<void> {
  const quote = await prisma.quote.findUnique({
    where: { id: quoteId },
    include: {
      trader: {
        select: {
          companyName: true,
        },
      },
    },
  });

  if (!quote) {
    throw new Error('Quote not found');
  }

  const message = `New freight lead available! ${quote.cargoName} from ${quote.pickupCity} to ${quote.deliveryCity}. Quantity: ${quote.quantity} ${quote.quantityUnit}. Ready: ${new Date(quote.cargoReadyDate).toLocaleDateString()}`;

  await sendMultiChannelNotification({
    userId: partnerId,
    title: ' New Lead Available',
    message,
    channels: ['EMAIL', 'SMS', 'WHATSAPP', 'PORTAL'],
    priority: 'HIGH',
    data: {
      quoteId: quote.id,
      quoteNumber: quote.quoteNumber,
      cargoName: quote.cargoName,
      pickupCity: quote.pickupCity,
      deliveryCity: quote.deliveryCity,
      quantity: quote.quantity,
      quantityUnit: quote.quantityUnit,
      cargoReadyDate: quote.cargoReadyDate,
      isHazardous: quote.isHazardous,
    },
  });
}

export async function notifyLowWalletBalance(
  partnerId: string,
  currentBalance: number,
  threshold: number
): Promise<void> {
  const message = ` Low wallet balance alert! Your current balance is ${currentBalance}. Please recharge to continue receiving leads.`;

  await sendMultiChannelNotification({
    userId: partnerId,
    title: 'Low Wallet Balance Alert',
    message,
    channels: ['EMAIL', 'SMS', 'PORTAL'],
    priority: 'URGENT',
    data: {
      currentBalance,
      threshold,
      recommendedRecharge: threshold * 2,
    },
  });
}

export async function notifyOfferExpiringSoon(
  quoteId: string,
  partnerId: string,
  expiresAt: Date
): Promise<void> {
  const minutesRemaining = Math.floor(
    (expiresAt.getTime() - Date.now()) / (1000 * 60)
  );

  const message = ` Quote submission deadline approaching! Only ${minutesRemaining} minutes remaining to submit your offer.`;

  await sendMultiChannelNotification({
    userId: partnerId,
    title: 'Quote Deadline Alert',
    message,
    channels: ['SMS', 'PORTAL'],
    priority: 'HIGH',
    data: {
      quoteId,
      expiresAt,
      minutesRemaining,
    },
  });
}

export async function sendLowBalanceAlert(
  userId: string,
  currentBalance: number
): Promise<void> {
  const threshold = 500;
  await notifyLowWalletBalance(userId, currentBalance, threshold);
}

lib/file-encryption.ts.content:
import crypto from 'crypto';
import fs from 'fs';
import path from 'path';

const ALGORITHM = 'aes-256-gcm';
const KEY_LENGTH = 32;
const IV_LENGTH = 16;
const AUTH_TAG_LENGTH = 16;

export function generateEncryptionKey(): string {
  return crypto.randomBytes(KEY_LENGTH).toString('base64');
}

export async function encryptFile(
  filePath: string,
  encryptionKey?: string
): Promise<{
  encryptedPath: string;
  encryptionKey: string;
  originalFileName: string;
}> {
  const key = encryptionKey
    ? Buffer.from(encryptionKey, 'base64')
    : crypto.randomBytes(KEY_LENGTH);

  const iv = crypto.randomBytes(IV_LENGTH);

  const cipher = crypto.createCipheriv(ALGORITHM, key, iv);

  const fileContent = await fs.promises.readFile(filePath);

  const encrypted = Buffer.concat([
    cipher.update(fileContent),
    cipher.final(),
  ]);

  const authTag = cipher.getAuthTag();

  const encryptedContent = Buffer.concat([
    iv,
    authTag,
    encrypted,
  ]);

  const encryptedFileName = `${crypto.randomBytes(16).toString('hex')}.enc`;
  const uploadsDir = path.join(process.cwd(), 'uploads', 'encrypted');

  await fs.promises.mkdir(uploadsDir, { recursive: true });

  const encryptedPath = path.join(uploadsDir, encryptedFileName);

  await fs.promises.writeFile(encryptedPath, encryptedContent);

  return {
    encryptedPath,
    encryptionKey: key.toString('base64'),
    originalFileName: path.basename(filePath),
  };
}

export async function decryptFile(
  encryptedPath: string,
  encryptionKey: string,
  outputPath?: string
): Promise<Buffer> {
  const key = Buffer.from(encryptionKey, 'base64');

  const encryptedContent = await fs.promises.readFile(encryptedPath);

  const iv = encryptedContent.subarray(0, IV_LENGTH);
  const authTag = encryptedContent.subarray(IV_LENGTH, IV_LENGTH + AUTH_TAG_LENGTH);
  const encrypted = encryptedContent.subarray(IV_LENGTH + AUTH_TAG_LENGTH);

  const decipher = crypto.createDecipheriv(ALGORITHM, key, iv);
  decipher.setAuthTag(authTag);

  const decrypted = Buffer.concat([
    decipher.update(encrypted),
    decipher.final(),
  ]);

  if (outputPath) {
    await fs.promises.writeFile(outputPath, decrypted);
  }

  return decrypted;
}

export async function encryptBuffer(
  buffer: Buffer,
  encryptionKey?: string
): Promise<{
  encrypted: Buffer;
  encryptionKey: string;
}> {
  const key = encryptionKey
    ? Buffer.from(encryptionKey, 'base64')
    : crypto.randomBytes(KEY_LENGTH);

  const iv = crypto.randomBytes(IV_LENGTH);

  const cipher = crypto.createCipheriv(ALGORITHM, key, iv);

  const encrypted = Buffer.concat([
    cipher.update(buffer),
    cipher.final(),
  ]);

  const authTag = cipher.getAuthTag();

  const encryptedContent = Buffer.concat([
    iv,
    authTag,
    encrypted,
  ]);

  return {
    encrypted: encryptedContent,
    encryptionKey: key.toString('base64'),
  };
}

export async function decryptBuffer(
  encryptedBuffer: Buffer,
  encryptionKey: string
): Promise<Buffer> {
  const key = Buffer.from(encryptionKey, 'base64');

  const iv = encryptedBuffer.subarray(0, IV_LENGTH);
  const authTag = encryptedBuffer.subarray(IV_LENGTH, IV_LENGTH + AUTH_TAG_LENGTH);
  const encrypted = encryptedBuffer.subarray(IV_LENGTH + AUTH_TAG_LENGTH);

  const decipher = crypto.createDecipheriv(ALGORITHM, key, iv);
  decipher.setAuthTag(authTag);

  const decrypted = Buffer.concat([
    decipher.update(encrypted),
    decipher.final(),
  ]);

  return decrypted;
}

export function validateEncryptionKey(key: string): boolean {
  try {
    const buffer = Buffer.from(key, 'base64');
    return buffer.length === KEY_LENGTH;
  } catch {
    return false;
  }
}

export async function secureDeleteFile(filePath: string): Promise<void> {
  const stats = await fs.promises.stat(filePath);
  const fileSize = stats.size;

  const randomData = crypto.randomBytes(fileSize);
  await fs.promises.writeFile(filePath, randomData);

  await fs.promises.unlink(filePath);
}

components/DocumentUpload.tsx.content:
import React, { useState } from 'react';
import Button from '@/components/ui/Button';
import { Alert } from '@/components/ui/Alert';

interface DocumentUploadProps {
  quoteId: string;
  token: string;
  onUploadComplete?: (documentId: string) => void;
}

export const DocumentUpload: React.FC<DocumentUploadProps> = ({
  quoteId,
  token,
  onUploadComplete,
}) => {
  const [file, setFile] = useState<File | null>(null);
  const [uploading, setUploading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState(false);
  const [documentType, setDocumentType] = useState('MSDS');

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      setFile(e.target.files[0]);
      setError(null);
      setSuccess(false);
    }
  };

  const handleUpload = async () => {
    if (!file) {
      setError('Please select a file to upload');
      return;
    }

    setUploading(true);
    setError(null);

    try {
      const formData = new FormData();
      formData.append('file', file);
      formData.append('quoteId', quoteId);
      formData.append('documentType', documentType);

      const response = await fetch('/api/documents/upload', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
        body: formData,
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Upload failed');
      }

      setSuccess(true);
      setFile(null);
      if (onUploadComplete) {
        onUploadComplete(data.document.id);
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Upload failed');
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="bg-white rounded-lg border border-gray-200 p-6">
      <h3 className="text-lg font-semibold text-gray-900 mb-4">
        Upload Safety Document
      </h3>

      {error && (
        <Alert variant="error" className="mb-4">
          {error}
        </Alert>
      )}

      {success && (
        <Alert variant="success" className="mb-4">
          Document uploaded and encrypted successfully! 
        </Alert>
      )}

      <div className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Document Type
          </label>
          <select
            value={documentType}
            onChange={(e) => setDocumentType(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="MSDS">MSDS (Material Safety Data Sheet)</option>
            <option value="SDS">SDS (Safety Data Sheet)</option>
            <option value="COA">COA (Certificate of Analysis)</option>
            <option value="OTHER">Other Safety Document</option>
          </select>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Select File
          </label>
          <input
            type="file"
            onChange={handleFileChange}
            accept=".pdf,.doc,.docx"
            className="w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-md file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
          />
          {file && (
            <p className="mt-2 text-sm text-gray-600">
              Selected: {file.name} ({(file.size / 1024).toFixed(2)} KB)
            </p>
          )}
        </div>

        <div className="flex items-center space-x-2 text-sm text-gray-600">
          <svg className="w-5 h-5 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" />
          </svg>
          <span>Files are encrypted with AES-256-GCM before storage</span>
        </div>

        <Button
          onClick={handleUpload}
          disabled={!file || uploading}
          variant="primary"
          className="w-full"
        >
          {uploading ? 'Uploading & Encrypting...' : 'Upload Document'}
        </Button>
      </div>
    </div>
  );
};

pages/partner/wallet.tsx.content:
import React, { useState, useEffect } from 'react';
import { Layout } from '@/components/layout/Layout';
import Card, { CardHeader, CardBody, CardTitle } from '@/components/ui/Card';
import Button from '@/components/ui/Button';
import Alert from '@/components/ui/Alert';
import Badge from '@/components/ui/Badge';
import { useAuth } from '@/contexts/AuthContext';

interface WalletData {
  balance: number;
  currency: string;
  lowBalanceAlert: boolean;
  alertThreshold: number;
  transactions: Transaction[];
}

interface Transaction {
  id: string;
  transactionType: string;
  amount: number;
  description: string;
  transactionDate: string;
  leadId?: string;
}

export default function PartnerWallet() {
  const { user, token } = useAuth();
  const [wallet, setWallet] = useState<WalletData | null>(null);
  const [loading, setLoading] = useState(true);
  const [rechargeAmount, setRechargeAmount] = useState('');
  const [alertThreshold, setAlertThreshold] = useState('');
  const [lowBalanceAlert, setLowBalanceAlert] = useState(true);
  const [showRechargeModal, setShowRechargeModal] = useState(false);
  const [processing, setProcessing] = useState(false);

  useEffect(() => {
    if (token) {
      fetchWallet();
    }
  }, [token]);

  const fetchWallet = async () => {
    try {
      const response = await fetch('/api/wallet', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      const data = await response.json();
      if (response.ok) {
        setWallet(data.wallet);
        setAlertThreshold(data.wallet.alertThreshold.toString());
        setLowBalanceAlert(data.wallet.lowBalanceAlert);
      }
    } catch (error) {
      console.error('Error fetching wallet:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleRecharge = async () => {
    const amount = parseFloat(rechargeAmount);
    if (isNaN(amount) || amount <= 0) {
      alert('Please enter a valid amount');
      return;
    }

    setProcessing(true);
    try {
      const response = await fetch('/api/wallet', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ amount, paymentMethod: 'Online' }),
      });

      if (response.ok) {
        setRechargeAmount('');
        setShowRechargeModal(false);
        fetchWallet();
      } else {
        const data = await response.json();
        alert(data.error || 'Recharge failed');
      }
    } catch (error) {
      alert('Recharge failed');
    } finally {
      setProcessing(false);
    }
  };

  const handleUpdateSettings = async () => {
    const threshold = parseFloat(alertThreshold);
    if (isNaN(threshold) || threshold < 0) {
      alert('Please enter a valid threshold');
      return;
    }

    setProcessing(true);
    try {
      const response = await fetch('/api/wallet/settings', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({
          lowBalanceAlert,
          alertThreshold: threshold,
        }),
      });

      if (response.ok) {
        alert('Settings updated successfully');
        fetchWallet();
      } else {
        const data = await response.json();
        alert(data.error || 'Update failed');
      }
    } catch (error) {
      alert('Update failed');
    } finally {
      setProcessing(false);
    }
  };

  if (!user || user.role !== 'LOGISTICS_PARTNER') {
    return (
      <Layout>
        <div className="text-center py-12">
          <p className="text-gray-600">Access denied. Partners only.</p>
        </div>
      </Layout>
    );
  }

  if (loading) {
    return (
      <Layout>
        <div className="text-center py-12">
          <div className="inline-block animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
        </div>
      </Layout>
    );
  }

  const isLowBalance = wallet && wallet.lowBalanceAlert && wallet.balance <= wallet.alertThreshold;

  return (
    <Layout>
      <div className="max-w-6xl mx-auto">
        <h1 className="text-3xl font-bold text-gray-900 mb-6">Lead Wallet</h1>

        {isLowBalance && (
          <Alert type="warning" className="mb-6">
             Low balance alert! Your current balance ({wallet?.balance.toLocaleString()}) is below your threshold ({wallet?.alertThreshold.toLocaleString()}). Please recharge to continue receiving leads.
          </Alert>
        )}

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-6">
          <Card className="lg:col-span-2">
            <CardHeader>
              <CardTitle>Current Balance</CardTitle>
            </CardHeader>
            <CardBody>
              <div className="text-center py-6">
                <p className="text-5xl font-bold text-blue-600 mb-2">
                  {wallet?.balance.toLocaleString() || '0'}
                </p>
                <p className="text-gray-600">Available Credits</p>
                <Button
                  variant="primary"
                  className="mt-6"
                  onClick={() => setShowRechargeModal(true)}
                >
                  Recharge Wallet
                </Button>
              </div>
            </CardBody>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>Alert Settings</CardTitle>
            </CardHeader>
            <CardBody>
              <div className="space-y-4">
                <div className="flex items-center">
