}

pages/trader/offers.tsx.content:
import React, { useState, useEffect } from 'react';
import { useRouter } from 'next/router';
import { Layout } from '@/components/layout/Layout';
import { Card, CardHeader, CardBody, CardTitle } from '@/components/ui/Card';
import Button from '@/components/ui/Button';
import { Badge } from '@/components/ui/Badge';
import { useAuth } from '@/contexts/AuthContext';

interface Offer {
  id: string;
  price: number;
  transitDays: number;
  offerValidUntil: string;
  pickupAvailableFrom: string;
  insuranceIncluded: boolean;
  trackingIncluded: boolean;
  customsClearance: boolean;
  valueAddedServices: string[];
  partner: {
    companyName: string;
    email: string;
  };
  quote: {
    quoteNumber: string;
    cargoName: string;
    pickupCity: string;
    deliveryCity: string;
  };
  status: string;
  submittedAt: string;
}

export default function TraderOffers() {
  const { user, token } = useAuth();
  const router = useRouter();
  const { quoteId } = router.query;
  const [offers, setOffers] = useState<Offer[]>([]);
  const [loading, setLoading] = useState(true);
  const [sortBy, setSortBy] = useState<'price' | 'transitDays'>('price');

  useEffect(() => {
    if (token && quoteId) {
      fetchOffers();
    }
  }, [token, quoteId]);

  const fetchOffers = async () => {
    try {
      const response = await fetch(`/api/offers?quoteId=${quoteId}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      const data = await response.json();
      if (response.ok) {
        setOffers(data.offers || []);
      }
    } catch (error) {
      console.error('Error fetching offers:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSelectOffer = async (offerId: string) => {
    if (!confirm('Are you sure you want to select this offer? This action cannot be undone.')) {
      return;
    }

    try {
      const response = await fetch(`/api/offers/${offerId}/select`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to select offer');
      }

      alert('Offer selected successfully! The partner will be notified.');
      router.push(`/quotes/${quoteId}`);
    } catch (error) {
      alert(error instanceof Error ? error.message : 'Failed to select offer');
    }
  };

  const sortedOffers = [...offers].sort((a, b) => {
    if (sortBy === 'price') {
      return a.price - b.price;
    }
    return a.transitDays - b.transitDays;
  });

  if (!user || user.role !== 'TRADER') {
    return (
      <Layout>
        <div className="text-center py-12">
          <p className="text-gray-600">Access denied. Traders only.</p>
        </div>
      </Layout>
    );
  }

  if (loading) {
    return (
      <Layout>
        <div className="text-center py-12">
          <div className="inline-block animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
        </div>
      </Layout>
    );
  }

  const selectedOffer = offers.find(o => o.status === 'ACCEPTED');

  return (
    <Layout>
      <div className="max-w-6xl mx-auto">
        <div className="flex items-center justify-between mb-6">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Compare Offers</h1>
            {offers.length > 0 && offers[0].quote && (
              <p className="text-gray-600 mt-1">
                For: {offers[0].quote.cargoName} ({offers[0].quote.quoteNumber})
              </p>
            )}
          </div>

          <div className="flex items-center space-x-3">
            <label className="text-sm font-medium text-gray-700">Sort by:</label>
            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value as 'price' | 'transitDays')}
              className="px-3 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
            >
              <option value="price">Lowest Price</option>
              <option value="transitDays">Fastest Delivery</option>
            </select>
          </div>
        </div>

        {selectedOffer && (
          <Card className="mb-6 border-2 border-green-500">
            <CardBody>
              <div className="flex items-center space-x-2 mb-2">
                <svg className="w-6 h-6 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
                <span className="text-lg font-semibold text-green-700">Selected Offer</span>
              </div>
              <p className="text-gray-600">
                Partner: <strong>{selectedOffer.partner.companyName}</strong> | 
                Price: <strong>{selectedOffer.price.toLocaleString()}</strong> | 
                Transit: <strong>{selectedOffer.transitDays} days</strong>
              </p>
            </CardBody>
          </Card>
        )}

        {offers.length === 0 ? (
          <Card>
            <CardBody className="text-center py-12">
              <svg className="mx-auto h-12 w-12 text-gray-400 mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
              </svg>
              <p className="text-gray-600 mb-2">No offers received yet</p>
              <p className="text-sm text-gray-500">Partners are reviewing your freight request</p>
            </CardBody>
          </Card>
        ) : (
          <div className="grid gap-4">
            {sortedOffers.map((offer, index) => (
              <Card key={offer.id} className={`${index === 0 && !selectedOffer ? 'border-blue-500 border-2' : ''}`}>
                <CardBody>
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <div className="flex items-center space-x-3 mb-3">
                        <h3 className="text-xl font-semibold text-gray-900">
                          {offer.partner.companyName}
                        </h3>
                        {index === 0 && !selectedOffer && (
                          <Badge variant="success">Best Value</Badge>
                        )}
                        {offer.status === 'ACCEPTED' && (
                          <Badge variant="success">Selected</Badge>
                        )}
                      </div>

                      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-4">
                        <div>
                          <p className="text-sm text-gray-500">Price</p>
                          <p className="text-2xl font-bold text-blue-600">
                            {offer.price.toLocaleString()}
                          </p>
                        </div>
                        <div>
                          <p className="text-sm text-gray-500">Transit Time</p>
                          <p className="text-lg font-semibold text-gray-900">
                            {offer.transitDays} days
                          </p>
                        </div>
                        <div>
                          <p className="text-sm text-gray-500">Pickup From</p>
                          <p className="font-medium text-gray-900">
                            {new Date(offer.pickupAvailableFrom).toLocaleDateString()}
                          </p>
                        </div>
                        <div>
                          <p className="text-sm text-gray-500">Valid Until</p>
                          <p className="font-medium text-gray-900">
                            {new Date(offer.offerValidUntil).toLocaleDateString()}
                          </p>
                        </div>
                      </div>

                      <div className="flex flex-wrap gap-2 mb-3">
                        {offer.insuranceIncluded && (
                          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
                             Insurance
                          </span>
                        )}
                        {offer.trackingIncluded && (
                          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                             Tracking
                          </span>
                        )}
                        {offer.customsClearance && (
                          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-purple-100 text-purple-800">
                             Customs
                          </span>
                        )}
                        {offer.valueAddedServices?.map((service, idx) => (
                          <span
                            key={idx}
                            className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-gray-100 text-gray-800"
                          >
                            {service}
                          </span>
                        ))}
                      </div>

                      <p className="text-xs text-gray-500">
                        Submitted: {new Date(offer.submittedAt).toLocaleString()}
                      </p>
                    </div>

                    <div className="ml-4">
                      {!selectedOffer && offer.status === 'PENDING' && (
                        <Button
                          variant="primary"
                          onClick={() => handleSelectOffer(offer.id)}
                        >
                          Select Offer
                        </Button>
                      )}
                    </div>
                  </div>
                </CardBody>
              </Card>
            ))}
          </div>
        )}
      </div>
    </Layout>
  );
}

pages/api/quotes/[id]/timer.ts.content:
import { NextApiRequest, NextApiResponse } from 'next';
import { withAuth, AuthenticatedRequest } from '@/lib/middleware';
import prisma from '@/lib/prisma';
import { UserRole } from '@prisma/client';
import { getRemainingTime, extendQuoteTimer } from '@/lib/quote-timer';

async function handler(req: AuthenticatedRequest, res: NextApiResponse) {
  const { id } = req.query;

  if (typeof id !== 'string') {
    return res.status(400).json({ error: 'Invalid quote ID' });
  }

  if (req.method === 'GET') {
    try {
      const quote = await prisma.quote.findUnique({
        where: { id },
        select: {
          id: true,
          traderId: true,
          expiresAt: true,
          status: true,
        },
      });

      if (!quote) {
        return res.status(404).json({ error: 'Quote not found' });
      }

      const isTrader = quote.traderId === req.user!.userId;
      const isAdmin = req.user!.role === UserRole.ADMIN;

      if (!isTrader && !isAdmin) {
        return res.status(403).json({ error: 'Access denied' });
      }

      if (!quote.expiresAt) {
        return res.status(400).json({ error: 'Quote does not have a timer' });
      }

      const timerInfo = await getRemainingTime(id);

      res.status(200).json({
        timer: {
          expiresAt: timerInfo.expiresAt,
          remainingMinutes: timerInfo.remainingMinutes,
          hasExpired: timerInfo.hasExpired,
        },
      });
    } catch (error) {
      console.error('Error fetching quote timer:', error);
      res.status(500).json({ error: 'Failed to fetch quote timer' });
    }
  } else if (req.method === 'POST') {
    try {
      if (req.user!.role !== UserRole.ADMIN) {
        return res.status(403).json({
          error: 'Only admins can extend quote timers',
        });
      }

      const { additionalMinutes } = req.body;

      if (
        !additionalMinutes ||
        typeof additionalMinutes !== 'number' ||
        additionalMinutes <= 0
      ) {
        return res.status(400).json({
          error: 'Invalid additional minutes',
        });
      }

      const newExpiresAt = await extendQuoteTimer(id, additionalMinutes);

      res.status(200).json({
        message: 'Quote timer extended successfully',
        newExpiresAt,
      });
    } catch (error) {
      console.error('Error extending quote timer:', error);
      res.status(500).json({ error: 'Failed to extend quote timer' });
    }
  } else {
    res.status(405).json({ error: 'Method not allowed' });
  }
}

export default withAuth(handler);

pages/api/wallet/settings.ts.content:
import { NextApiResponse } from 'next';
import prisma from '@/lib/prisma';
import { withAuth, AuthenticatedRequest } from '@/lib/middleware';
import { UserRole } from '@prisma/client';
import { updateWalletAlertSettings } from '@/lib/wallet-alerts';

async function handler(req: AuthenticatedRequest, res: NextApiResponse) {
  if (req.user!.role !== UserRole.LOGISTICS_PARTNER) {
    return res.status(403).json({
      error: 'Only logistics partners can manage wallet settings',
    });
  }

  if (req.method === 'GET') {
    try {
      const wallet = await prisma.leadWallet.findUnique({
        where: { userId: req.user!.userId },
        select: {
          lowBalanceAlert: true,
          alertThreshold: true,
          balance: true,
        },
      });

      if (!wallet) {
        return res.status(404).json({ error: 'Wallet not found' });
      }

      res.status(200).json({ settings: wallet });
    } catch (error) {
      console.error('Error fetching wallet settings:', error);
      res.status(500).json({ error: 'Failed to fetch wallet settings' });
    }
  } else if (req.method === 'PUT') {
    try {
      const { lowBalanceAlert, alertThreshold } = req.body;

      if (alertThreshold !== undefined) {
        if (
          typeof alertThreshold !== 'number' ||
          alertThreshold < 0 ||
          alertThreshold > 1000000
        ) {
          return res.status(400).json({
            error: 'Invalid alert threshold (must be between 0 and 1,000,000)',
          });
        }
      }

      const settings: any = {};

      if (lowBalanceAlert !== undefined) {
        settings.lowBalanceAlert = Boolean(lowBalanceAlert);
      }

      if (alertThreshold !== undefined) {
        settings.alertThreshold = alertThreshold;
      }

      await updateWalletAlertSettings(req.user!.userId, settings);

      const updatedWallet = await prisma.leadWallet.findUnique({
        where: { userId: req.user!.userId },
        select: {
          lowBalanceAlert: true,
          alertThreshold: true,
          balance: true,
        },
      });

      res.status(200).json({
        settings: updatedWallet,
        message: 'Wallet settings updated successfully',
      });
    } catch (error) {
      console.error('Error updating wallet settings:', error);
      res.status(500).json({ error: 'Failed to update wallet settings' });
    }
  } else {
    res.status(405).json({ error: 'Method not allowed' });
  }
}

export default withAuth(handler);

components/CountdownTimer.tsx.content:
import React, { useState, useEffect } from 'react';

interface CountdownTimerProps {
  expiresAt: string;
  onExpire?: () => void;
  showSeconds?: boolean;
}

export const CountdownTimer: React.FC<CountdownTimerProps> = ({ 
  expiresAt, 
  onExpire,
  showSeconds = true 
}) => {
  const [timeRemaining, setTimeRemaining] = useState<number>(0);
  const [hasExpired, setHasExpired] = useState(false);

  useEffect(() => {
    const calculateTimeRemaining = () => {
      const now = new Date().getTime();
      const expiry = new Date(expiresAt).getTime();
      const remaining = Math.max(0, expiry - now);

      setTimeRemaining(remaining);

      if (remaining === 0 && !hasExpired) {
        setHasExpired(true);
        if (onExpire) {
          onExpire();
        }
      }
    };

    calculateTimeRemaining();
    const interval = setInterval(calculateTimeRemaining, 1000);

    return () => clearInterval(interval);
  }, [expiresAt, hasExpired, onExpire]);

  const formatTime = (ms: number) => {
    const totalSeconds = Math.floor(ms / 1000);
    const hours = Math.floor(totalSeconds / 3600);
    const minutes = Math.floor((totalSeconds % 3600) / 60);
    const seconds = totalSeconds % 60;

    if (showSeconds) {
      return `${hours.toString().padStart(2, '0')}:${minutes
        .toString()
        .padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
    }

    return `${hours}h ${minutes}m`;
  };

  const getColorClass = () => {
    const minutes = Math.floor(timeRemaining / 1000 / 60);
    if (minutes <= 0) return 'text-red-600 bg-red-50';
    if (minutes <= 10) return 'text-orange-600 bg-orange-50';
    if (minutes <= 30) return 'text-yellow-600 bg-yellow-50';
    return 'text-green-600 bg-green-50';
  };

  if (hasExpired || timeRemaining === 0) {
    return (
      <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-semibold bg-red-100 text-red-800">
        Expired
      </span>
    );
  }

  return (
    <span className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-semibold ${getColorClass()}`}>
       {formatTime(timeRemaining)}
    </span>
  );
};

