import { NextApiRequest, NextApiResponse } from 'next';
import { authenticateUser } from '@/lib/middleware';
import prisma from '@/lib/prisma';
import { PaymentRequestStatus, UserRole } from '@prisma/client';

export default async function handler(
  req: NextApiRequest,
  res: NextApiResponse
) {
  try {
    const user = await authenticateUser(req);
    if (!user) {
      return res.status(401).json({ error: 'Unauthorized' });
    }

    if (req.method === 'GET') {
      if (user.role === UserRole.ADMIN) {
        const { status } = req.query;

        const requests = await prisma.paymentRequest.findMany({
          where: status ? { status: status as PaymentRequestStatus } : {},
          include: {
            user: {
              select: {
                id: true,
                email: true,
                companyName: true,
                role: true,
              },
            },
          },
          orderBy: { createdAt: 'desc' },
        });

        return res.status(200).json({ requests });
      } else {
        const requests = await prisma.paymentRequest.findMany({
          where: { userId: user.id },
          orderBy: { createdAt: 'desc' },
        });

        return res.status(200).json({ requests });
      }
    }

    if (req.method === 'POST') {
      if (user.role !== UserRole.LOGISTICS_PARTNER) {
        return res.status(403).json({ error: 'Only logistics partners can request wallet recharges' });
      }

      const {
        amount,
        paymentMethod,
        referenceNumber,
        transactionId,
        paymentDate,
        requestNotes,
      } = req.body;

      if (!amount || amount <= 0) {
        return res.status(400).json({ error: 'Invalid amount' });
      }

      if (!paymentMethod) {
        return res.status(400).json({ error: 'Payment method is required' });
      }

      const paymentRequest = await prisma.paymentRequest.create({
        data: {
          userId: user.id,
          amount,
          paymentMethod,
          referenceNumber,
          transactionId,
          paymentDate: paymentDate ? new Date(paymentDate) : null,
          requestNotes,
        },
      });

      await prisma.auditLog.create({
        data: {
          userId: user.id,
          action: 'CREATE_PAYMENT_REQUEST',
          entity: 'PaymentRequest',
          entityId: paymentRequest.id,
          changes: { amount, paymentMethod },
        },
      });

      return res.status(201).json({
        message: 'Payment request submitted successfully',
        request: paymentRequest,
      });
    }

    return res.status(405).json({ error: 'Method not allowed' });
  } catch (error) {
    console.error('Payment request error:', error);
    return res.status(500).json({ error: 'Internal server error' });
  }
}

pages/admin/payments.tsx.content:
import React, { useEffect, useState } from 'react';
import { Layout } from '@/components/layout/Layout';
import { useAuth } from '@/contexts/AuthContext';
import { Card, Badge, Button, Table, Modal, Alert } from '@/components/ui';
import { useRouter } from 'next/router';

export default function AdminPayments() {
  const { user, token } = useAuth();
  const router = useRouter();
  const [requests, setRequests] = useState<any[]>([]);
  const [filteredRequests, setFilteredRequests] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('PENDING');
  const [selectedRequest, setSelectedRequest] = useState<any>(null);
  const [showModal, setShowModal] = useState(false);
  const [reviewNotes, setReviewNotes] = useState('');
  const [actionType, setActionType] = useState<'APPROVED' | 'REJECTED'>('APPROVED');
  const [processing, setProcessing] = useState(false);
  const [alert, setAlert] = useState<{ type: 'success' | 'error'; message: string } | null>(null);

  useEffect(() => {
    if (!user || user.role !== 'ADMIN') {
      router.push('/');
      return;
    }

    fetchPaymentRequests();
  }, [user, token]);

  useEffect(() => {
    if (filter === 'ALL') {
      setFilteredRequests(requests);
    } else {
      setFilteredRequests(requests.filter((r) => r.status === filter));
    }
  }, [filter, requests]);

  const fetchPaymentRequests = async () => {
    try {
      const response = await fetch('/api/payment-requests', {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.ok) {
        const data = await response.json();
        setRequests(data.requests || []);
      }
    } catch (error) {
      console.error('Error fetching payment requests:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleReviewRequest = async () => {
    if (!selectedRequest) return;

    setProcessing(true);
    try {
      const response = await fetch(`/api/payment-requests/${selectedRequest.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          status: actionType,
          reviewNotes,
        }),
      });

      if (response.ok) {
        setAlert({
          type: 'success',
          message: `Payment request ${actionType.toLowerCase()} successfully`,
        });
        setShowModal(false);
        setReviewNotes('');
        setSelectedRequest(null);
        fetchPaymentRequests();
      } else {
        const data = await response.json();
        setAlert({ type: 'error', message: data.error || 'Failed to review request' });
      }
    } catch (error) {
      setAlert({ type: 'error', message: 'An error occurred' });
    } finally {
      setProcessing(false);
    }
  };

  const openModal = (request: any, action: 'APPROVED' | 'REJECTED') => {
    setSelectedRequest(request);
    setActionType(action);
    setShowModal(true);
    setReviewNotes('');
  };

  const columns = [
    { key: 'id', header: 'Request ID', render: (row: any) => row.id.slice(0, 8) },
    {
      key: 'user',
      header: 'Partner',
      render: (row: any) => (
        <div>
          <div className="font-medium">{row.user.companyName || row.user.email}</div>
          <div className="text-sm text-gray-500">{row.user.email}</div>
        </div>
      ),
    },
    {
      key: 'amount',
      header: 'Amount',
      render: (row: any) => (
        <span className="font-semibold text-green-600">
          {row.amount.toLocaleString()}
        </span>
      ),
    },
    {
      key: 'paymentMethod',
      header: 'Method',
      render: (row: any) => row.paymentMethod.replace(/_/g, ' '),
    },
    {
      key: 'referenceNumber',
      header: 'Reference',
      render: (row: any) => row.referenceNumber || '-',
    },
    {
      key: 'createdAt',
      header: 'Submitted',
      render: (row: any) => new Date(row.createdAt).toLocaleDateString(),
    },
    {
      key: 'status',
      header: 'Status',
      render: (row: any) => {
        const statusColors: any = {
          PENDING: 'yellow',
          APPROVED: 'green',
          REJECTED: 'red',
        };
        return <Badge variant={statusColors[row.status]}>{row.status}</Badge>;
      },
    },
    {
      key: 'actions',
      header: 'Actions',
      render: (row: any) =>
        row.status === 'PENDING' ? (
          <div className="flex gap-2">
            <Button
              size="sm"
              variant="primary"
              onClick={() => openModal(row, 'APPROVED')}
            >
              Approve
            </Button>
            <Button
              size="sm"
              variant="secondary"
              onClick={() => openModal(row, 'REJECTED')}
            >
              Reject
            </Button>
          </div>
        ) : (
          <span className="text-sm text-gray-500">-</span>
        ),
    },
  ];

  if (loading) {
    return (
      <Layout>
        <div className="text-center py-12">Loading...</div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="max-w-7xl mx-auto">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Payment Requests</h1>
          <p className="text-gray-600">Review and approve wallet recharge requests from partners</p>
        </div>

        {alert && (
          <Alert type={alert.type} className="mb-6" onClose={() => setAlert(null)}>
            {alert.message}
          </Alert>
        )}

        <Card className="mb-6">
          <div className="flex gap-4 mb-4">
            <Button
              variant={filter === 'PENDING' ? 'primary' : 'secondary'}
              onClick={() => setFilter('PENDING')}
            >
              Pending ({requests.filter((r) => r.status === 'PENDING').length})
            </Button>
            <Button
              variant={filter === 'APPROVED' ? 'primary' : 'secondary'}
              onClick={() => setFilter('APPROVED')}
            >
              Approved ({requests.filter((r) => r.status === 'APPROVED').length})
            </Button>
            <Button
              variant={filter === 'REJECTED' ? 'primary' : 'secondary'}
              onClick={() => setFilter('REJECTED')}
            >
              Rejected ({requests.filter((r) => r.status === 'REJECTED').length})
            </Button>
            <Button
              variant={filter === 'ALL' ? 'primary' : 'secondary'}
              onClick={() => setFilter('ALL')}
            >
              All ({requests.length})
            </Button>
          </div>

          {filteredRequests.length > 0 ? (
            <Table columns={columns} data={filteredRequests} />
          ) : (
            <div className="text-center py-12 text-gray-500">
              No {filter.toLowerCase()} payment requests found
            </div>
          )}
        </Card>
      </div>

      {showModal && selectedRequest && (
        <Modal
          isOpen={showModal}
          onClose={() => setShowModal(false)}
          title={`${actionType === 'APPROVED' ? 'Approve' : 'Reject'} Payment Request`}
        >
          <div className="space-y-4">
            <div className="bg-gray-50 p-4 rounded">
              <div className="grid grid-cols-2 gap-4 text-sm">
                <div>
                  <span className="text-gray-600">Partner:</span>
                  <div className="font-medium">{selectedRequest.user.companyName}</div>
                </div>
                <div>
                  <span className="text-gray-600">Amount:</span>
                  <div className="font-semibold text-green-600">
                    {selectedRequest.amount.toLocaleString()}
                  </div>
                </div>
                <div>
                  <span className="text-gray-600">Method:</span>
                  <div className="font-medium">
                    {selectedRequest.paymentMethod.replace(/_/g, ' ')}
                  </div>
                </div>
                <div>
                  <span className="text-gray-600">Reference:</span>
                  <div className="font-medium">{selectedRequest.referenceNumber || '-'}</div>
                </div>
              </div>
              {selectedRequest.requestNotes && (
                <div className="mt-3 pt-3 border-t">
                  <span className="text-gray-600">Request Notes:</span>
                  <div className="mt-1">{selectedRequest.requestNotes}</div>
                </div>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Review Notes {actionType === 'REJECTED' && '(Required for rejection)'}
              </label>
              <textarea
                value={reviewNotes}
                onChange={(e) => setReviewNotes(e.target.value)}
                className="w-full border border-gray-300 rounded-lg px-4 py-2"
                rows={3}
                placeholder="Add notes about your decision..."
              />
            </div>

            <div className="flex justify-end gap-3">
              <Button variant="secondary" onClick={() => setShowModal(false)}>
                Cancel
              </Button>
              <Button
                variant={actionType === 'APPROVED' ? 'primary' : 'secondary'}
                onClick={handleReviewRequest}
                disabled={processing || (actionType === 'REJECTED' && !reviewNotes.trim())}
              >
                {processing ? 'Processing...' : `${actionType === 'APPROVED' ? 'Approve' : 'Reject'} Request`}
              </Button>
            </div>
          </div>
        </Modal>
      )}
    </Layout>
  );
}

pages/api/socket.ts.content:
import { NextApiRequest } from 'next';
import { NextApiResponseServerIO } from '@/types/socket';
import { Server as HTTPServer } from 'http';
import { initializeWebSocket } from '@/lib/websocket-server';

export const config = {
  api: {
    bodyParser: false,
  },
};

export default function handler(
  req: NextApiRequest,
  res: NextApiResponseServerIO
) {
  if (!res.socket.server.io) {
    console.log('Initializing WebSocket server...');

    const httpServer: HTTPServer = res.socket.server as any;
    const io = initializeWebSocket(httpServer);

    res.socket.server.io = io;
  } else {
    console.log('WebSocket server already initialized');
  }

  res.end();
}

components/ui/PasswordInput.tsx.content:
import React, { useState } from 'react';

interface PasswordInputProps extends React.InputHTMLAttributes<HTMLInputElement> {
  label?: string;
  error?: string;
  showRequirements?: boolean;
  value?: string;
}

export const PasswordInput: React.FC<PasswordInputProps> = ({
  label,
  error,
  showRequirements = false,
  value = '',
  className = '',
  ...props
}) => {
  const [showPassword, setShowPassword] = useState(false);

  const requirements = [
    { label: 'At least 8 characters', met: value.length >= 8 },
    { label: 'One uppercase letter', met: /[A-Z]/.test(value) },
    { label: 'One lowercase letter', met: /[a-z]/.test(value) },
    { label: 'One number', met: /[0-9]/.test(value) },
    { label: 'One special character', met: /[!@#$%^&*(),.?":{}|<>]/.test(value) },
  ];

  return (
    <div className="w-full">
      {label && (
        <label className="block text-sm font-medium text-gray-700 mb-1">
          {label}
        </label>
      )}
      <div className="relative">
        <input
          type={showPassword ? 'text' : 'password'}
          value={value}
          className={`w-full px-4 py-2 border rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent pr-10 ${
            error ? 'border-red-500' : 'border-gray-300'
          } ${className}`}
          {...props}
        />
        <button
          type="button"
          onClick={() => setShowPassword(!showPassword)}
          className="absolute right-3 top-1/2 -translate-y-1/2 text-gray-500 hover:text-gray-700"
          tabIndex={-1}
        >
          {showPassword ? (
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13.875 18.825A10.05 10.05 0 0112 19c-4.478 0-8.268-2.943-9.543-7a9.97 9.97 0 011.563-3.029m5.858.908a3 3 0 114.243 4.243M9.878 9.878l4.242 4.242M9.88 9.88l-3.29-3.29m7.532 7.532l3.29 3.29M3 3l3.59 3.59m0 0A9.953 9.953 0 0112 5c4.478 0 8.268 2.943 9.543 7a10.025 10.025 0 01-4.132 5.411m0 0L21 21" />
            </svg>
          ) : (
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
            </svg>
          )}
        </button>
      </div>

      {error && (
        <p className="mt-1 text-sm text-red-600">{error}</p>
      )}

      {showRequirements && value && (
        <div className="mt-3 space-y-2">
          <p className="text-sm font-medium text-gray-700">Password must contain:</p>
          {requirements.map((req, index) => (
            <div key={index} className="flex items-center text-sm">
              {req.met ? (
                <svg className="w-4 h-4 text-green-500 mr-2" fill="currentColor" viewBox="0 0 20 20">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                </svg>
              ) : (
                <svg className="w-4 h-4 text-gray-400 mr-2" fill="currentColor" viewBox="0 0 20 20">
