import React, { useState, useEffect, useRef } from 'react';

interface CountdownTimerProps {
  expiresAt: Date | string;
  onExpire?: () => void;
  showLabel?: boolean;
}

export const CountdownTimer: React.FC<CountdownTimerProps> = ({
  expiresAt,
  onExpire,
  showLabel = true,
}) => {
  const [timeRemaining, setTimeRemaining] = useState<number>(0);
  const hasExpiredRef = useRef<boolean>(false);

  useEffect(() => {
    const calculateTimeRemaining = () => {
      const now = new Date().getTime();
      const expiry = new Date(expiresAt).getTime();
      const remaining = expiry - now;

      if (remaining <= 0) {
        setTimeRemaining(0);
        if (!hasExpiredRef.current && onExpire) {
          hasExpiredRef.current = true;
          onExpire();
        }
        return 0;
      } else {
        if (hasExpiredRef.current) {
          hasExpiredRef.current = false;
        }
        return remaining;
      }
    };

    setTimeRemaining(calculateTimeRemaining());

    const interval = setInterval(() => {
      const remaining = calculateTimeRemaining();
      setTimeRemaining(remaining);

      if (remaining <= 0) {
        clearInterval(interval);
      }
    }, 1000);

    return () => clearInterval(interval);
  }, [expiresAt, onExpire]);

  const formatTime = (milliseconds: number) => {
    const totalSeconds = Math.floor(milliseconds / 1000);
    const hours = Math.floor(totalSeconds / 3600);
    const minutes = Math.floor((totalSeconds % 3600) / 60);
    const seconds = totalSeconds % 60;

    if (hours > 0) {
      return `${hours}h ${minutes}m`;
    } else if (minutes > 0) {
      return `${minutes}m ${seconds}s`;
    } else {
      return `${seconds}s`;
    }
  };

  const getColorClass = () => {
    const minutesRemaining = timeRemaining / (1000 * 60);

    if (timeRemaining === 0) {
      return 'bg-gray-100 text-gray-600 border-gray-300';
    } else if (minutesRemaining < 10) {
      return 'bg-red-50 text-red-700 border-red-300 animate-pulse';
    } else if (minutesRemaining < 30) {
      return 'bg-yellow-50 text-yellow-700 border-yellow-300';
    } else {
      return 'bg-green-50 text-green-700 border-green-300';
    }
  };

  const getIcon = () => {
    if (timeRemaining === 0) {
      return '';
    }
    const minutesRemaining = timeRemaining / (1000 * 60);
    if (minutesRemaining < 10) {
      return '';
    } else if (minutesRemaining < 30) {
      return '';
    } else {
      return '';
    }
  };

  const expiryDate = new Date(expiresAt);

  return (
    <div className={`inline-flex items-center px-4 py-3 rounded-lg border-2 ${getColorClass()}`}>
      <span className="text-2xl mr-2">{getIcon()}</span>
      <div>
        {showLabel && (
          <p className="text-xs font-medium uppercase tracking-wide opacity-75">
            {timeRemaining === 0 ? 'Expired' : 'Time Remaining'}
          </p>
        )}
        <p className="text-lg font-bold">
          {timeRemaining === 0 ? 'Quote Expired' : formatTime(timeRemaining)}
        </p>
        <p className="text-xs opacity-75">
          Expires: {expiryDate.toLocaleString('en-IN', {
            month: 'short',
            day: 'numeric',
            hour: '2-digit',
            minute: '2-digit',
          })}
        </p>
      </div>
    </div>
  );
};

export default CountdownTimer;

lib/pagination.ts.content:
export interface PaginationParams {
  page?: number;
  limit?: number;
  sortBy?: string;
  sortOrder?: 'asc' | 'desc';
}

export interface PaginatedResult<T> {
  data: T[];
  pagination: {
    page: number;
    limit: number;
    total: number;
    totalPages: number;
    hasMore: boolean;
  };
}

export function getPaginationParams(query: any): Required<PaginationParams> {
  const page = Math.max(1, parseInt(query.page as string) || 1);
  const limit = Math.min(100, Math.max(1, parseInt(query.limit as string) || 20));
  const sortBy = (query.sortBy as string) || 'createdAt';
  const sortOrder = (query.sortOrder === 'asc' ? 'asc' : 'desc') as 'asc' | 'desc';

  return { page, limit, sortBy, sortOrder };
}

export function calculatePagination(page: number, limit: number, total: number) {
  const totalPages = Math.ceil(total / limit);
  const hasMore = page < totalPages;

  return {
    page,
    limit,
    total,
    totalPages,
    hasMore,
  };
}

export function applyPagination<T>(
  data: T[],
  page: number,
  limit: number
): PaginatedResult<T> {
  const total = data.length;
  const startIndex = (page - 1) * limit;
  const endIndex = startIndex + limit;
  const paginatedData = data.slice(startIndex, endIndex);

  return {
    data: paginatedData,
    pagination: calculatePagination(page, limit, total),
  };
}

export function buildPrismaOrderBy(sortBy: string, sortOrder: 'asc' | 'desc') {
  return { [sortBy]: sortOrder };
}

export function buildPrismaSkipTake(page: number, limit: number) {
  return {
    skip: (page - 1) * limit,
    take: limit,
  };
}

lib/websocket-server.ts.content:
import { Server as HTTPServer } from 'http';
import { Server as SocketIOServer } from 'socket.io';
import { verifyToken } from './auth';

let io: SocketIOServer | null = null;

export function initializeWebSocket(httpServer: HTTPServer) {
  if (io) {
    return io;
  }

  io = new SocketIOServer(httpServer, {
    cors: {
      origin: process.env.NEXT_PUBLIC_APP_URL || 'http://localhost:5000',
      methods: ['GET', 'POST'],
      credentials: true,
    },
  });

  io.use((socket, next) => {
    const token = socket.handshake.auth.token;

    if (!token) {
      return next(new Error('Authentication required'));
    }

    const payload = verifyToken(token);

    if (!payload) {
      return next(new Error('Invalid token'));
    }

    socket.data.user = payload;
    next();
  });

  io.on('connection', (socket) => {
    const user = socket.data.user;
    console.log(`User connected: ${user.email} (${user.role})`);

    socket.join(`user:${user.userId}`);

    if (user.role === 'LOGISTICS_PARTNER') {
      socket.join('partners');
    } else if (user.role === 'TRADER') {
      socket.join('traders');
    } else if (user.role === 'ADMIN') {
      socket.join('admins');
    }

    socket.on('disconnect', () => {
      console.log(`User disconnected: ${user.email}`);
    });
  });

  console.log(' WebSocket server initialized');
  return io;
}

export function getIO(): SocketIOServer {
  if (!io) {
    throw new Error('WebSocket not initialized');
  }
  return io;
}

export function emitToUser(userId: string, event: string, data: any) {
  if (io) {
    io.to(`user:${userId}`).emit(event, data);
  }
}

export function emitToPartners(event: string, data: any) {
  if (io) {
    io.to('partners').emit(event, data);
  }
}

export function emitToTraders(event: string, data: any) {
  if (io) {
    io.to('traders').emit(event, data);
  }
}

export function emitToAdmins(event: string, data: any) {
  if (io) {
    io.to('admins').emit(event, data);
  }
}

export function broadcastQuoteUpdate(quoteId: string, data: any) {
  if (io) {
    io.emit(`quote:${quoteId}:update`, data);
  }
}

export function broadcastNewOffer(quoteId: string, offer: any) {
  if (io) {
    io.emit(`quote:${quoteId}:new-offer`, offer);
  }
}

