import { NextApiRequest, NextApiResponse } from 'next';
import { authenticateUser } from '@/lib/middleware';
import prisma from '@/lib/prisma';
import { UserRole, QuoteStatus, PaymentRequestStatus } from '@prisma/client';
import { getOrSetCache, cacheKey } from '@/lib/cache';

export default async function handler(
  req: NextApiRequest,
  res: NextApiResponse
) {
  try {
    const user = await authenticateUser(req);
    if (!user || user.role !== UserRole.ADMIN) {
      return res.status(403).json({ error: 'Admin access required' });
    }

    if (req.method !== 'GET') {
      return res.status(405).json({ error: 'Method not allowed' });
    }

    const stats = await getOrSetCache(
      cacheKey('admin', 'stats'),
      async () => {
        const [
          totalQuotes,
          totalOffers,
          totalShipments,
          activePartners,
          totalRevenue,
          pendingPayments,
          quotesByStatus,
        ] = await Promise.all([
          prisma.quote.count(),
          prisma.offer.count(),
          prisma.shipment.count(),
          prisma.user.count({ where: { role: UserRole.LOGISTICS_PARTNER, isActive: true } }),
          prisma.leadTransaction.aggregate({
            _sum: { amount: true },
            where: { transactionType: 'DEBIT' },
          }),
          prisma.paymentRequest.count({ where: { status: PaymentRequestStatus.PENDING } }),
          prisma.quote.groupBy({
            by: ['status'],
            _count: true,
          }),
        ]);

        const quoteStatusMap: Record<string, number> = {};
        quotesByStatus.forEach((item: any) => {
          quoteStatusMap[item.status] = item._count;
        });

        return {
          totalQuotes,
          totalOffers,
          totalShipments,
          activePartners,
          totalRevenue: totalRevenue._sum.amount || 0,
          pendingPayments,
          quotesByStatus: quoteStatusMap,
        };
      },
      300000
    );

    return res.status(200).json(stats);
  } catch (error) {
    console.error('Admin stats error:', error);
    return res.status(500).json({ error: 'Internal server error' });
  }
}

lib/cache.ts.content:
interface CacheEntry<T> {
  data: T;
  timestamp: number;
  ttl: number;
}

class SimpleCache {
  private cache: Map<string, CacheEntry<any>> = new Map();

  set<T>(key: string, data: T, ttl: number = 300000): void {
    this.cache.set(key, {
      data,
      timestamp: Date.now(),
      ttl,
    });
  }

  get<T>(key: string): T | null {
    const entry = this.cache.get(key);

    if (!entry) {
      return null;
    }

    if (Date.now() - entry.timestamp > entry.ttl) {
      this.cache.delete(key);
      return null;
    }

    return entry.data as T;
  }

  delete(key: string): void {
    this.cache.delete(key);
  }

  clear(): void {
    this.cache.clear();
  }

  invalidatePattern(pattern: string): void {
    const regex = new RegExp(pattern);

    for (const key of this.cache.keys()) {
      if (regex.test(key)) {
        this.cache.delete(key);
      }
    }
  }

  cleanup(): void {
    const now = Date.now();

    for (const [key, entry] of this.cache.entries()) {
      if (now - entry.timestamp > entry.ttl) {
        this.cache.delete(key);
      }
    }
  }
}

export const cache = new SimpleCache();

setInterval(() => {
  cache.cleanup();
}, 60000);

export function cacheKey(...parts: (string | number)[]): string {
  return parts.join(':');
}

export async function getOrSetCache<T>(
  key: string,
  fetchFn: () => Promise<T>,
  ttl: number = 300000
): Promise<T> {
  const cached = cache.get<T>(key);

  if (cached !== null) {
    return cached;
  }

  const data = await fetchFn();
  cache.set(key, data, ttl);
  return data;
}

components/Dashboard/ChartCard.tsx.content:
import React from 'react';
import Card from '../ui/Card';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
  Filler,
} from 'chart.js';
import { Line, Bar, Doughnut } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
  Filler
);

interface ChartCardProps {
  title: string;
  type: 'line' | 'bar' | 'doughnut';
  data: any;
  options?: any;
  height?: number;
}

export const ChartCard: React.FC<ChartCardProps> = ({
  title,
  type,
  data,
  options = {},
  height = 300,
}) => {
  const defaultOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'bottom' as const,
      },
      title: {
        display: false,
      },
    },
    ...options,
  };

  const renderChart = () => {
    switch (type) {
      case 'line':
        return <Line data={data} options={defaultOptions} />;
      case 'bar':
        return <Bar data={data} options={defaultOptions} />;
      case 'doughnut':
        return <Doughnut data={data} options={defaultOptions} />;
      default:
        return null;
    }
  };

  return (
    <Card>
      <h3 className="text-lg font-semibold text-gray-900 mb-4">{title}</h3>
      <div style={{ height: `${height}px` }}>{renderChart()}</div>
    </Card>
  );
};

export default ChartCard;

pages/partner/recharge.tsx.content:
import React, { useState, useEffect } from 'react';
import { Layout } from '@/components/layout/Layout';
import { useAuth } from '@/contexts/AuthContext';
import { Card, Button, Input, Select, Alert, Badge, Table } from '@/components/ui';
import { useRouter } from 'next/router';

export default function PartnerRecharge() {
  const { user, token } = useAuth();
  const router = useRouter();
  const [amount, setAmount] = useState('');
  const [paymentMethod, setPaymentMethod] = useState('BANK_TRANSFER');
  const [referenceNumber, setReferenceNumber] = useState('');
  const [transactionId, setTransactionId] = useState('');
  const [paymentDate, setPaymentDate] = useState('');
  const [requestNotes, setRequestNotes] = useState('');
  const [submitting, setSubmitting] = useState(false);
  const [alert, setAlert] = useState<{ type: 'success' | 'error'; message: string } | null>(null);
  const [requests, setRequests] = useState<any[]>([]);

  useEffect(() => {
    if (!user || user.role !== 'LOGISTICS_PARTNER') {
      router.push('/');
      return;
    }

    fetchPaymentRequests();
  }, [user, token]);

  const fetchPaymentRequests = async () => {
    try {
      const response = await fetch('/api/payment-requests', {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.ok) {
        const data = await response.json();
        setRequests(data.requests || []);
      }
    } catch (error) {
      console.error('Error fetching payment requests:', error);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!amount || parseFloat(amount) <= 0) {
      setAlert({ type: 'error', message: 'Please enter a valid amount' });
      return;
    }

    setSubmitting(true);
    setAlert(null);

    try {
      const response = await fetch('/api/payment-requests', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          amount: parseFloat(amount),
          paymentMethod,
          referenceNumber,
          transactionId,
          paymentDate: paymentDate || null,
          requestNotes,
        }),
      });

      if (response.ok) {
        setAlert({
          type: 'success',
          message: 'Recharge request submitted successfully! Admin will review it shortly.',
        });
        setAmount('');
        setReferenceNumber('');
        setTransactionId('');
        setPaymentDate('');
        setRequestNotes('');
        fetchPaymentRequests();
      } else {
        const data = await response.json();
        setAlert({ type: 'error', message: data.error || 'Failed to submit request' });
      }
    } catch (error) {
      setAlert({ type: 'error', message: 'An error occurred' });
    } finally {
      setSubmitting(false);
    }
  };

  const paymentMethods = [
    { value: 'BANK_TRANSFER', label: 'Bank Transfer' },
    { value: 'UPI', label: 'UPI' },
    { value: 'CHEQUE', label: 'Cheque' },
    { value: 'ONLINE', label: 'Online Payment' },
  ];

  const columns = [
    { key: 'id', header: 'Request ID', render: (row: any) => row.id.slice(0, 8) },
    {
      key: 'amount',
      header: 'Amount',
      render: (row: any) => `${row.amount.toLocaleString()}`,
    },
    { key: 'paymentMethod', header: 'Method', render: (row: any) => row.paymentMethod.replace(/_/g, ' ') },
    { key: 'referenceNumber', header: 'Reference', render: (row: any) => row.referenceNumber || '-' },
    {
      key: 'createdAt',
      header: 'Submitted',
      render: (row: any) => new Date(row.createdAt).toLocaleDateString(),
    },
    {
      key: 'status',
      header: 'Status',
      render: (row: any) => {
        const colors: any = {
          PENDING: 'yellow',
          APPROVED: 'green',
          REJECTED: 'red',
        };
        return <Badge variant={colors[row.status]}>{row.status}</Badge>;
      },
    },
    {
      key: 'reviewNotes',
      header: 'Admin Notes',
      render: (row: any) => row.reviewNotes || '-',
    },
  ];

  return (
    <Layout>
      <div className="max-w-7xl mx-auto">
        <h1 className="text-3xl font-bold text-gray-900 mb-8">Wallet Recharge</h1>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          <Card>
            <h2 className="text-xl font-semibold mb-6">Submit Recharge Request</h2>

            {alert && (
              <Alert type={alert.type} className="mb-4" onClose={() => setAlert(null)}>
                {alert.message}
              </Alert>
            )}

            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Amount () *
                </label>
                <Input
                  type="number"
                  value={amount}
                  onChange={(e) => setAmount(e.target.value)}
                  placeholder="Enter amount"
                  min="1"
                  step="0.01"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Payment Method *
                </label>
                <Select
                  value={paymentMethod}
                  onChange={(e) => setPaymentMethod(e.target.value)}
                  options={paymentMethods}
                />
              </div>

              <div>
