import { useState, useEffect, useCallback } from 'react';

interface AutosaveOptions {
  key: string;
  debounceMs?: number;
  onSave?: (data: any) => Promise<void>;
}

export function useAutosave<T extends Record<string, any>>(
  initialData: T,
  options: AutosaveOptions
) {
  const { key, debounceMs = 2000, onSave } = options;
  const [data, setData] = useState<T>(initialData);
  const [isDirty, setIsDirty] = useState(false);
  const [isSaving, setIsSaving] = useState(false);
  const [lastSaved, setLastSaved] = useState<Date | null>(null);

  useEffect(() => {
    const saved = localStorage.getItem(key);
    if (saved) {
      try {
        const parsedData = JSON.parse(saved);
        setData(parsedData);
      } catch (error) {
        console.error('Failed to parse saved data:', error);
      }
    }
  }, [key]);

  useEffect(() => {
    if (!isDirty) return;

    const timeoutId = setTimeout(async () => {
      setIsSaving(true);

      try {
        localStorage.setItem(key, JSON.stringify(data));

        if (onSave) {
          await onSave(data);
        }

        setLastSaved(new Date());
        setIsDirty(false);
      } catch (error) {
        console.error('Autosave failed:', error);
      } finally {
        setIsSaving(false);
      }
    }, debounceMs);

    return () => clearTimeout(timeoutId);
  }, [data, isDirty, key, debounceMs, onSave]);

  const updateData = useCallback((updates: Partial<T>) => {
    setData((prev) => ({ ...prev, ...updates }));
    setIsDirty(true);
  }, []);

  const clearSaved = useCallback(() => {
    localStorage.removeItem(key);
    setData(initialData);
    setIsDirty(false);
    setLastSaved(null);
  }, [key, initialData]);

  return {
    data,
    updateData,
    setData,
    isDirty,
    isSaving,
    lastSaved,
    clearSaved,
  };
}

pages/api/quotes/paginated.ts.content:
import { NextApiRequest, NextApiResponse } from 'next';
import { authenticateUser } from '@/lib/middleware';
import prisma from '@/lib/prisma';
import { getPaginationParams, buildPrismaOrderBy, buildPrismaSkipTake, calculatePagination } from '@/lib/pagination';
import { cacheKey, getOrSetCache, cache } from '@/lib/cache';

export default async function handler(
  req: NextApiRequest,
  res: NextApiResponse
) {
  try {
    const user = await authenticateUser(req);
    if (!user) {
      return res.status(401).json({ error: 'Unauthorized' });
    }

    if (req.method !== 'GET') {
      return res.status(405).json({ error: 'Method not allowed' });
    }

    const { page, limit, sortBy, sortOrder } = getPaginationParams(req.query);
    const { status } = req.query;

    const where: any = {};

    if (user.role === 'TRADER') {
      where.traderId = user.userId;
    }

    if (status) {
      where.status = status;
    }

    const cacheKeyStr = cacheKey('quotes', user.userId, page, limit, sortBy, sortOrder, status || 'all');

    const result = await getOrSetCache(
      cacheKeyStr,
      async () => {
        const [quotes, total] = await Promise.all([
          prisma.quote.findMany({
            where,
            include: {
              trader: {
                select: {
                  id: true,
                  email: true,
                  companyName: true,
                },
              },
              offers: {
                select: {
                  id: true,
                  partnerId: true,
                  price: true,
                  status: true,
                },
              },
            },
            orderBy: buildPrismaOrderBy(sortBy, sortOrder),
            ...buildPrismaSkipTake(page, limit),
          }),
          prisma.quote.count({ where }),
        ]);

        return {
          quotes,
          pagination: calculatePagination(page, limit, total),
        };
      },
      60000
    );

    return res.status(200).json(result);
  } catch (error) {
    console.error('Paginated quotes error:', error);
    return res.status(500).json({ error: 'Internal server error' });
  }
}

