import { NextApiRequest, NextApiResponse } from 'next';
import { authenticateUser } from '@/lib/middleware';
import prisma from '@/lib/prisma';
import { UserRole, PaymentRequestStatus, TransactionType } from '@prisma/client';

export default async function handler(
  req: NextApiRequest,
  res: NextApiResponse
) {
  try {
    const user = await authenticateUser(req);
    if (!user) {
      return res.status(401).json({ error: 'Unauthorized' });
    }

    const { id } = req.query;

    if (req.method === 'GET') {
      const request = await prisma.paymentRequest.findUnique({
        where: { id: id as string },
        include: {
          user: {
            select: {
              id: true,
              email: true,
              companyName: true,
              role: true,
            },
          },
        },
      });

      if (!request) {
        return res.status(404).json({ error: 'Payment request not found' });
      }

      if (user.role !== UserRole.ADMIN && request.userId !== user.id) {
        return res.status(403).json({ error: 'Access denied' });
      }

      return res.status(200).json({ request });
    }

    if (req.method === 'PUT') {
      if (user.role !== UserRole.ADMIN) {
        return res.status(403).json({ error: 'Only admins can approve or reject payment requests' });
      }

      const { status, reviewNotes } = req.body;

      if (!['APPROVED', 'REJECTED'].includes(status)) {
        return res.status(400).json({ error: 'Invalid status. Must be APPROVED or REJECTED' });
      }

      const paymentRequest = await prisma.paymentRequest.findUnique({
        where: { id: id as string },
      });

      if (!paymentRequest) {
        return res.status(404).json({ error: 'Payment request not found' });
      }

      if (paymentRequest.status !== PaymentRequestStatus.PENDING) {
        return res.status(400).json({ error: 'Payment request has already been reviewed' });
      }

      if (status === 'APPROVED') {
        let wallet = await prisma.leadWallet.findUnique({
          where: { userId: paymentRequest.userId },
        });

        if (!wallet) {
          wallet = await prisma.leadWallet.create({
            data: {
              userId: paymentRequest.userId,
              balance: 0,
            },
          });
        }

        const result = await prisma.$transaction(async (tx) => {
          const updatedRequest = await tx.paymentRequest.update({
            where: { id: id as string },
            data: {
              status: PaymentRequestStatus.APPROVED,
              reviewedBy: user.id,
              reviewedAt: new Date(),
              reviewNotes,
            },
          });

          const updatedWallet = await tx.leadWallet.update({
            where: { userId: paymentRequest.userId },
            data: {
              balance: { increment: paymentRequest.amount },
            },
          });

          const transaction = await tx.leadTransaction.create({
            data: {
              walletId: wallet.id,
              transactionType: TransactionType.CREDIT,
              amount: paymentRequest.amount,
              description: `Manual recharge approved by admin (Ref: ${paymentRequest.referenceNumber || 'N/A'})`,
            },
          });

          await tx.auditLog.create({
            data: {
              userId: user.id,
              action: 'APPROVE_PAYMENT_REQUEST',
              entity: 'PaymentRequest',
              entityId: paymentRequest.id,
              changes: { 
                amount: paymentRequest.amount, 
                userId: paymentRequest.userId,
                newBalance: updatedWallet.balance 
              },
            },
          });

          return { updatedRequest, updatedWallet, transaction };
        });

        return res.status(200).json({
          message: 'Payment request approved and wallet credited successfully',
          newBalance: result.updatedWallet.balance,
          transaction: result.transaction,
        });
      } else {
        await prisma.paymentRequest.update({
          where: { id: id as string },
          data: {
            status: PaymentRequestStatus.REJECTED,
            reviewedBy: user.id,
            reviewedAt: new Date(),
            reviewNotes,
          },
        });

        await prisma.auditLog.create({
          data: {
            userId: user.id,
            action: 'REJECT_PAYMENT_REQUEST',
            entity: 'PaymentRequest',
            entityId: paymentRequest.id,
            changes: { reviewNotes },
          },
        });

        return res.status(200).json({
          message: 'Payment request rejected',
        });
      }
    }

    return res.status(405).json({ error: 'Method not allowed' });
  } catch (error) {
    console.error('Payment request action error:', error);
    return res.status(500).json({ error: 'Internal server error' });
  }
}

lib/notification-providers.ts.content:
import { env, servicesAvailable } from './env-validation';

export interface EmailParams {
  to: string;
  subject: string;
  html: string;
  text?: string;
}

export interface SMSParams {
  to: string;
  body: string;
}

export interface WhatsAppParams {
  to: string;
  body: string;
}

export async function sendEmail(params: EmailParams): Promise<boolean> {
  if (!servicesAvailable.email) {
    console.log('[EMAIL MOCK]', params.to, params.subject);
    return true;
  }

  try {
    const sgMail = require('@sendgrid/mail');
    sgMail.setApiKey(env.SENDGRID_API_KEY);

    await sgMail.send({
      to: params.to,
      from: 'noreply@bidchemz.com',
      subject: params.subject,
      html: params.html,
      text: params.text || params.subject,
    });

    console.log(' Email sent to:', params.to);
    return true;
  } catch (error) {
    console.error(' Email sending failed:', error);
    return false;
  }
}

export async function sendSMS(params: SMSParams): Promise<boolean> {
  if (!servicesAvailable.sms) {
    console.log('[SMS MOCK]', params.to, params.body);
    return true;
  }

  try {
    const twilio = require('twilio');
    const client = twilio(env.TWILIO_ACCOUNT_SID, env.TWILIO_AUTH_TOKEN);

    await client.messages.create({
      to: params.to,
      from: env.TWILIO_PHONE_NUMBER,
      body: params.body,
    });

    console.log(' SMS sent to:', params.to);
    return true;
  } catch (error) {
    console.error(' SMS sending failed:', error);
    return false;
  }
}

export async function sendWhatsApp(params: WhatsAppParams): Promise<boolean> {
  if (!servicesAvailable.whatsapp) {
    console.log('[WHATSAPP MOCK]', params.to, params.body);
    return true;
  }

  try {
    const twilio = require('twilio');
    const client = twilio(env.TWILIO_ACCOUNT_SID, env.TWILIO_AUTH_TOKEN);

    await client.messages.create({
      to: `whatsapp:${params.to}`,
      from: env.TWILIO_WHATSAPP_NUMBER,
      body: params.body,
    });

    console.log(' WhatsApp message sent to:', params.to);
    return true;
  } catch (error) {
    console.error(' WhatsApp sending failed:', error);
    return false;
  }
}

export async function sendMultiChannelNotification(
  user: { email: string; phone?: string },
  notification: {
    subject: string;
    emailBody: string;
    smsBody?: string;
    priority?: 'LOW' | 'MEDIUM' | 'HIGH' | 'URGENT';
  }
): Promise<{
  email: boolean;
  sms: boolean;
  whatsapp: boolean;
}> {
  const results = {
    email: false,
    sms: false,
    whatsapp: false,
  };

  results.email = await sendEmail({
    to: user.email,
    subject: notification.subject,
    html: notification.emailBody,
  });

  if (user.phone && notification.smsBody) {
    results.sms = await sendSMS({
      to: user.phone,
      body: notification.smsBody,
    });

    if (notification.priority === 'HIGH' || notification.priority === 'URGENT') {
      results.whatsapp = await sendWhatsApp({
        to: user.phone,
        body: notification.smsBody,
      });
    }
  }

  return results;
}

